INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (312, 'SR', '', 'Описание Трусы BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 840 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME 2S_SR.png', null, 'SUPREME 2S', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (313, 'SR', '', 'Описание Трусы BAUER VAPOR 2X, профессиональная модель линейки VAPOR 2020 года. Данные трусы предлагают максимальную эффективность, защиту и комфорт. Защита позвоночника X-LITE SHIELD будет двигаться вместе с вами, в то время как система регулировки FAST STRAP снимает нагрузку с живота. Область тазобедренных суставов и бедра выполнены из полиэтилена. Так же трусы имеют высокопрочный чехол, выполненный из нейлона профессионального уровня плотностью 840 ден. Для вашего удобства в трусах предусмотрена система регулировки, которая позволяет увеличить трусы на 1 дюйм. Внутренняя подкладка выполнена с применением технологии THERMO MAX+, которая эффективно отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Удобство – Клиновидное ✅Защита позвоночника – X-LITE SHIELD ✅Защита почек – Пена высокой плотности ✅Защита тазобедренная – Литой полиэтилен ✅Защита бедра – Полиэтилен ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Трусы/BAUER_VAPOR 2X_SR.png', null, 'VAPOR 2X', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (314, 'JR', '', 'Описание Трусы CCM SUPER TACKS AS1, топовая модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется плавающая конструкция с пеной D3O, которая рассеивает энергию удара, эта же пена используется для защиты тазобедренных суставов тела. В области бедра для максимальной защиты используется пена высокой плотности и пластик. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Элитный ✅Удобство – Анатомическое ✅Защита позвоночника – D3O ✅Защита почек – Пластик ✅Защита тазобедренная – JDP; D30 ✅Защита бедра – Пластик; Пена высокой плотности ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Трусы/CCM_SUPER TACKS AS1_JR.png', null, 'SUPER TACKS AS1', 2, 8);
