INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (369, 'YTH', '', 'Описание Перчатки BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Данные перчатки предназначены для детей начинающего уровня. Перчатки имеют удобную посадку и обеспечивают хорошую защиту. Защитные сегменты выполнены из пены двойной плотности. Гибкая манжета позволяет запястью свободно двигаться. Внутренняя подкладка из гидрофобной сетки способствует быстрому высыханию. Особенности: ✅Уровень – Любительский ✅Удобство – Клиновидное ✅Внешний материал – Высокопрочная нить CABLE MESH ✅Защита тыльной стороны – Пена средней плотности ✅Защита пальцев – Пена средней плотности ✅Ладонь – NASH ✅Подкладка – HYDROPHOBIC Характеристики Возрастная группа YTH', '/root/images/Перчатки/BAUER_NSX_YTH.png', null, 'NSX', 1, 11);
