INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (371, 'JR', '', 'Описание Перчатки CCM TACKS 9040, любительская модель линейки CCM 2019 года. Перчатки TACKS 9040 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки полиэстер, который придает перчаткам легкий вес. Защита пальцев и тыльной стороны перчатки выполнена из пенополиэтилена, которая обладает легким весом и хорошими защитными функциями. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: • Уровень – Любительский • Внешний материал – Полиэстер • Защита тыльной стороны – Пенополиэтилен • Ладонь –NASH Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 9040_JR.png', null, 'TACKS 9040', 2, 11);
