INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (384, 'SR', '', 'Описание Перчатки BAUER VAPOR X900 Если игра зависит от скорости ваших рук, перчатки VAPOR X900 — идеальное решение именно для вас. Конусовидная форма для лучшего владения клюшкой и широкая манжета Free Flex обеспечивают подвижность запястья. Более плотное расположение пальцев и ладонная часть AX SUEDE™ QUATTRO Grip позволяют лучше чувствовать клюшку. Общая посадка: • Зауженная Пальцы: • Трехкомпонентная часть для указательного пальца Ладонная часть: • AX SUEDE™ QUATTRO Grip для ладони — покрытие из искусственной замши в командных цветах Манжета: • Манжета Free Flex Подкладка: • Технология 37,5® Вставки: • Полиэтилен Пеноматериалы: • Тройная плотность Щиток для тыльной стороны кисти: • Пена Comfort Большой палец: • Запатентованный двухкомпонентный фиксатор большого пальца Чехол: • Синтетическая кожа + проволочная сетка Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Перчатки/BAUER_VAPOR X900_SR.png', null, 'VAPOR X900', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (385, 'SR', '', 'Описание Перчатки CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Данная модель выполнена из высококачественного сетчатого материала PRO MESH, который придает перчаткам прочность и легкость. Для защиты тыльной части и пальцев используется вставки из полиэтилена и пластика. Материал ладошки NASH с дополнительной накладкой. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Высококачественный сетчатый материал PRO MESH • Защита тыльной стороны – Полиэтилен; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – NASH • Подкладка – Полиуретан PU Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Перчатки/CCM_JETSPEED FT370_SR.png', null, 'JETSPEED FT370', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (386, 'JR', '', 'Описание Перчатки BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2020 года. Данные перчатки сочетают в себе отличную посадку и максимальную защиту ключевых зон. Все защитные элементы перчаток выполнены из пены средней плотности с добавлением полиэтилена, благодаря этому перчатки имеет хорошие защитные свойства. У перчаток клиновидное удобство, которое характеризуется более плотным прилегание в области пальцев и свободным в области манжеты. Внешний материал перчаток выполнен из плетеной сетки с добавлением полиуретана. Ладонь у перчаток выполнена из материала TAKTILE, которая очень мягкая на ощупь, долговечна и обеспечивает максимальный уровень комфорта. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая эффективно отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Клиновидное ✅Внешний материал – Плетеная сетка; полиуретан ✅Защита тыльной стороны – Пена средней плотности; Полиэтилен ✅Защита пальцев – Пена средней плотности; Полиэтилен ✅Большой палец – Двухсоставной ✅Ладонь – TAKTILE ✅Подкладка – THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Перчатки/BAUER_VAPOR X2.9_JR.png', null, 'VAPOR X2.9', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (387, 'JR', '', 'Описание Перчатки CCM TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Перчатки TACKS 9060 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из легкого и прочного полиэстера. Защита пальцев и тыльной стороны перчатки выполнена из пены двойной плотности, и полиэтиленовых вставок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Полиэстер • Защита тыльной стороны –Пена двойной плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –NASH • Подкладка – POLYGIENE Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 9060_JR.png', null, 'TACKS 9060', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (388, 'SR', '', 'Описание Перчатки EAGLE X905. Особенности: • Три части боковой защиты указательного пальца • Усиленная пластиком тыльная часть перчатки • Конструкция разделенных пальцев добавляет гибкость • Угловые задние ролики для дополнительной гибкости • Ручная работа с вышитым принтом • Верх перчатки из материала Tuff-Tek для легкости • Неподвижный большой палец • Новая ладонь MSH3 со вставками • Ручная работа сделано в Канаде Характеристики Возрастная группа SR', '/root/images/Перчатки/EAGLE_X905_SR.png', null, 'X905', 15, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (389, 'SR', '', 'Описание Перчатки BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Модель имеет клиновидную посадку, которая отличается плотным прилеганием в области пальцев и более свободным в области манжеты. Внешний материал перчатки сделан из высокопрочной нейлоновой сетки PRO NYLON MESH и CABLE MESH . Внутренняя подкладка THERMO MAX обработанная по технологии SANITIZED®, которая препятствует возникновению неприятных запахов и распространению бактерий. Особенности: • Уровень – Профессиональный • Удобство – Клиновидное • Внешний материал – Высокопрочная нейлоновая сетка; плетеная сетка • Защита тыльной стороны – Пена двойной плотности • Защита пальцев – Пена двойной плотности • Большой палец – Запатентованная технология FLEX-LOCK ( состоит из 2-х частей ) • Ладонь – QUATTRO GRIP AX SUEDE • Подкладка – THERMO MAX; SANITIZED® Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Перчатки/BAUER_VAPOR X900 LITE_SR.png', null, 'VAPOR X900 LITE', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (390, 'SR', '', 'Описание Перчатки WARRIOR ALPHA DX 3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Идеальный баланс классического дизайна и современных знаний об анатомии позволяет улучшить контроль шайбы и хорошо защитить руки • HYBRID TAPER FIT – Позволяет перчаткам повторять контур руки, максимально увеличивая мобильность и улучшая защиту • BUTTER SOFT FEEL - Лайкровые вставки в пальцах, технология AXI-Flex сгибаемый большой палец и анатомическая верхняя тянущаяся поверхность перчаток ALPHA обеспечивает легкость, комфорт и контроль • PREMIUM PROTECTION – Защитная пена и пластиковые вставки обеспечивают лучшую защиту • PRO PALM – Ладошка ,сочетающая в себе прочность и комфорт • LINER– Ярко-желтая подкладка обеспечивает сухость рук Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Перчатки/WARRIOR_ALPHA DX3_SR.png', null, 'ALPHA DX3', 3, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (391, 'JR', '', 'Описание Перчатки BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки. Манжета выполнена по технологии AMP благодаря чему вы можете свободно двигать запястьем. Ладонь, выполненная из материала AX SUEDE QUATTRO, обеспечивает вам комфорт и позволяет лучше чувствовать клюшку. Внутренняя подкладка THERMO MAX и подкладка в области пальцев HYPERSENSE сохраняют ваши руки в сухости и придают максимальный комфорт. Особенности: • Уровень – Полупрофессиональный • Удобство – Анатомическое • Внешний материал – Плетеная сетка CABLE MESH; Нейлон • Защита тыльной стороны – Пена двойной плотности; вставки из пластика • Защита пальцев – Пена средней плотности; вставки из пластика • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 2-х частей) • Ладонь – AX SUEDE QUATTRO • Подкладка – THERMO MAX; HYPERSENSE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Перчатки/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (392, 'JR', '', 'Описание Перчатки CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Тыльная сторона перчатки для максимальной защиты, выполнена из пены высокой плотности и пластиковых вставок PE, которые имеют очень легкий вес и хорошие защитные функции. Ладонь выполнена из материала CLARINO с дополнительной вставкой, которая обладает хорошей износостойкостью, а специально, разработанный рельефный узор служит для лучшего сцепления и чувствительности клюшки. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Профессиональный • Внешний материал – Высококачественный полиэстер • Защита тыльной стороны – Пена высокой плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – CLARINO • Подкладка – Полиуретан PU; POLYGIENE Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Перчатки/CCM_JETSPEED FT390_JR.png', null, 'JETSPEED FT390', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (393, 'SR', '', 'Описание Перчатки BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2020 года. Данные перчатки сочетают в себе отличную посадку и максимальную защиту ключевых зон. Все защитные элементы перчаток выполнены из пены средней плотности с добавлением полиэтилена, благодаря этому перчатки имеет хорошие защитные свойства. У перчаток клиновидное удобство, которое характеризуется более плотным прилегание в области пальцев и свободным в области манжеты. Внешний материал перчаток выполнен из плетеной сетки с добавлением полиуретана. Ладонь у перчаток выполнена из материала TAKTILE, которая очень мягкая на ощупь, долговечна и обеспечивает максимальный уровень комфорта. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая эффективно отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Клиновидное ✅Внешний материал – Плетеная сетка; полиуретан ✅Защита тыльной стороны – Пена средней плотности; Полиэтилен ✅Защита пальцев – Пена средней плотности; Полиэтилен ✅Большой палец – Двухсоставной ✅Ладонь – TAKTILE ✅Подкладка – THERMO MAX Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Перчатки/BAUER_VAPOR X2.9_SR.png', null, 'VAPOR X2.9', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (394, 'SR', '', 'Описание Перчатки CCM TACKS 4R PRO, профессиональная модель линейки TACKS 2018 года. Перчатки имеют классическую посадку с широким местом в области пальцев и запястья. Внешний материал перчаток выполнен из высококачественного полиэстера с вставками из искусственной кожи. Защита пальцев и тыльной стороны перчатки выполнена из пены средней плотности и вставок из пластика. Ладонь выполнена из материала высококачественного материала NASH c дополнительным усилением в зонах повышенного воздействия. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: ✅Уровень – Профессиональный ✅Внешний материал – Высококачественный полиэстер ✅Защита тыльной стороны – Пена средней плотности; Вставки из пластика PE ✅Большой палец – Двухсекционный, подвижный (ТОЛЬКО В РАЗМЕРЕ SR) ✅Ладонь – NASH ✅Подкладка – POLYGIENE Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 4R PRO_SR.png', null, 'TACKS 4R PRO', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (395, 'SR', '', 'Описание Перчатки CCM TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Перчатки TACKS 9060 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из легкого и прочного полиэстера. Защита пальцев и тыльной стороны перчатки выполнена из пены двойной плотности, и полиэтиленовых вставок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Полиэстер • Защита тыльной стороны –Пена двойной плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –NASH • Подкладка – POLYGIENE Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 9060_SR.png', null, 'TACKS 9060', 2, 11);
