INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (397, 'JR', '', 'Описание Перчатки CCM TACKS 9080, профессиональная модель линейки CCM 2019 года. Перчатки TACKS 9080 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из очень легкого и прочного материала PRO SELECTED POLYESTER. Защита пальцев и тыльной стороны перчатки выполнена из пены высокой плотности, технологии D3O,которая максимально поглощает энергию, и полиэтиленовых вставок. Ладонь выполнена из материала PALM CLARINO, которая обладает максимальным удобством, а дополнительная накладка из армированного кевлара, обеспечивает прочность. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Профессиональный • Внешний материал – PRO SELECTED POLYESTER • Защита тыльной стороны – D3O; Пена высокой плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –PALM CLARINO • Подкладка – POLYGIENE Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 9080_JR.png', null, 'TACKS 9080', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (398, 'SR', '', 'Описание Перчатки WARRIOR ALPHA DX PRO, профессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Идеальный баланс классического дизайна и современных знаний об анатомии позволяет улучшить контроль шайбы и хорошо защитить руки • HYBRID TAPER FIT – Позволяет перчаткам повторять контур руки, максимально увеличивая мобильность и улучшая защиту • HYBRID FLEX CUFF – Расширяет диапазон движения, улучшая обработку шайбы, при этом защищает кисти от ударов • BUTTER SOFT FEEL - Лайкровые вставки в пальцах, технология AXI-Flex сгибаемый большой палец и анатомическая верхняя тянущаяся поверхность перчаток ALPHA обеспечивает легкость, комфорт и контроль • IMPAX - Смесь высококачественной пены, в сочетании защитными вставками, придают перчаткам легкий вес и поглощают удары • PRO PALM – Ладошка ,сочетающая в себе прочность и комфорт • WARTECH FNC LINER – Ярко-желтая подкладка Polygiene, обеспечивает сухость и защиту от запаха Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Перчатки/WARRIOR_ALPHA DX PRO_SR.png', null, 'ALPHA DX PRO', 3, 11);
