INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (413, 'SR', '', 'Описание Перчатки BAUER NEXUS 2N, топовая модель линейки NEXUS 2018 года. Перчатки 2N имеют классическую форму. Внешний материал перчатки сделан из высокопрочной нейлоновой сетки PRO NYLON MESH и CABLE MESH. Трехкомпонентная запатентованная защита большого пальца FLEX-LOCK. Ладонь у перчаток выполнена из материала QUATTRO GRIP AX SUEDE, которая дает игроку отличное ощущение клюшки и долговечность. Так же ладонь дополнительно усилена в стратегически важном месте. Внутренняя подкладка 37.5® обработанная по технологии SANITIZED®, которая препятствует возникновению неприятных запахов и распространению бактерий. Особенности: • Уровень – Элитный • Удобство – Классическое, широкое • Внешний материал – Высокопрочная нейлоновая сетка; плетеная сетка • Защита тыльной стороны – Пена двойной плотности • Защита пальцев – Пена двойной плотности • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 3-х частей) • Ладонь – QUATTRO GRIP AX SUEDE • Подкладка – 37,5®; SANITIZED® Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Перчатки/BAUER_NEXUS 2N_SR.png', null, 'NEXUS 2N', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (414, 'SR', '', 'Описание Перчатки BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки и излом в области фаланговых суставов для максимальной подвижности и удобства. Манжета выполнена по технологии AMP благодаря чему вы можете свободно двигать запястьем. Ладонь, выполненная из материала AX SUEDE QUATTRO, обеспечивает вам комфорт, а благодаря эластичным вставкам COMFORT STRETCH FIT безупречно передает тактильные ощущения. Внутренняя подкладка THERMO MAX и подкладка в области пальцев HYPERSENSE сохраняют ваши руки в сухости и придают максимальный комфорт. Особенности: • Уровень – Профессиональный • Удобство – Анатомическое • Внешний материал – Плетеная сетка CABLE MESH; Нейлон • Защита тыльной стороны – Пена двойной плотности; вставки из пластика • Защита пальцев – Пена средней плотности; вставки из пластика • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 2-х частей) • Ладонь – AX SUEDE QUATTRO • Подкладка – THERMO MAX; HYPERSENSE Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Перчатки/BAUER_SUPREME 2S_SR.png', null, 'SUPREME 2S', 1, 11);
