INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (416, 'JR', '', 'Описание Перчатки BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки и излом в области фаланговых суставов для максимальной подвижности и удобства. Данная модель обеспечивает максимальный уровень защиты благодаря пене тройной плотности и материалу PORON XRD. Трехсегметная конструкция большого и указательного пальцев делает перчатку максимально удобной и подвижной. Манжета выполнена по технологии AMP благодаря чему вы можете свободно двигать запястьем. Ладонь, выполненная из материала PRO PALM, обеспечивает превосходную передачу тактильные ощущения. Внутренняя подкладка THERMOCORE и подкладка в области пальцев HYPERSENSE сохраняют ваши руки в сухости и придают максимальный комфорт. Особенности: • Уровень – Элитный • Удобство – Анатомическое • Внешний материал – Нейлоновая сетка; Искусственная кожа • Защита тыльной стороны – Пена тройной плотности SHOX; PORON XRD • Защита пальцев – Пена тройной плотности SHOX • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 3-х частей) • Ладонь – PRO PALM • Подкладка – THERMOCORE; HYPERSENSE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Перчатки/BAUER_SUPREME 2S PRO_JR.png', null, 'SUPREME 2S PRO', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (417, 'JR', '', 'Описание Перчатки BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2020 года. Самые лёгкие перчатки на рынке - 265 грамм!!! Перчатки разработаны с использованием высокотехнологичной пены SHOCKLITE для обеспечения профессионального уровня защиты с непревзойденной чувствительностью. У перчаток клиновидное удобство, которое характеризуется более плотным прилегание в области пальцев и свободным в области манжеты. Для максимальной защиты пальцев используется пена HYPERLITE. Ладонь у перчаток выполнена из сверхчувствительного материала CONNECT+, которая обеспечивает превосходную обратную связь от клюшки. Так же ладонь дополнительно усилена в стратегически важном месте. Внутренняя подкладка выполнена с применением технологии THERMO CORE, которая эффективно отводит влагу, предотвращает рост бактерий и возникновение неприятного запаха. Особенности: ✅Уровень – Элитный ✅Удобство – Клиновидное ✅Внешний материал – Высокопрочная плетеная сетка; Полиуретан ✅Защита тыльной стороны – Пена SHOCKLITE ✅Защита пальцев – HYPERLITE ✅Большой палец – Двухсоставной ✅Ладонь – CONNECT+ ✅Подкладка – THERMO CORE Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Перчатки/BAUER_VAPOR 2X PRO_JR.png', null, 'VAPOR 2X PRO', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (418, 'SR', '', 'Описание Перчатки CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Благодаря внешнему покрытию перчаток из высококачественного полиэстера и искусственной кожи перчатки очень прочны и износостойки. В перчатки используется гибкая манжета для лучшей подвижности руки. Тыльная сторона перчатки для максимальной защиты, выполнена из трех разных технологий. В первом слое использована пена премиум класса ZOTEFOAM, которая не впитывает влагу и сохраняет перчатки легкий вес. Следующий слой выполнен из пены D3O, которая обладает высокой степенью поглощения удара. И третий слой это пластиковые вставки PE, которые имеют очень легкий вес и хорошие защитные функции. Ладонь выполнена из материала PRO PALM CLARINO, которая обладает хорошей износостойкостью, а специально, разработанный рельефный узор служит для лучшего сцепления и чувствительности клюшки. Внутренний материал пенополиуретан PU выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Элитный • Внешний материал – Искусственная кожа; высококачественный полиэстер • Защита тыльной стороны – D3O; ZOTEFOAM; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – PRO PALM CLARINO • Подкладка – Полиуретан PU;POLYGIENE Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Перчатки/CCM_JETSPEED FT1_SR.png', null, 'JETSPEED FT1', 2, 11);
