INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (421, 'SR', '', 'Описание Клюшка WARRIOR ALPHA DX5, любительская модель новой линейки Warrior 2019 года. ✅SABER TAPER – Уникальная конструкция клиновидного сужения создает жесткую к скручиванию зону прогиба клюшки, что делает крюк более стабильным для большей точности броска ✅MINIMUS CARBON – Высокопрочная, отзывчивая и легкая конструкция из цельнолитого углеродистого композита улучшает все технологические свойства клюшки ✅ERGO SHAFT SHARE – Эргономичная форма шафта клюшки удобно и надежно ложится в руки, повышая чувствительность, чтобы вы могли легко совершать броски, финты и убирать шайбу под себя ✅APEX GRIP – Нескользящее мягкое покрытие клюшки- легко удерживать в руках для оптимального контроля и точности Полная длина клюшки - 165 см Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Клюшка/WARRIOR_ALPHA DX5 GRIP_SR.png', null, 'ALPHA DX5 GRIP', 3, 13);
