INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (423, 'YTH', '', 'Описание Клюшка CCM JETSPEED 440 GRIP YTH, детская любительская модель линейки JETSPEED 2019 года. Клюшка данной модели имеет гибридную точку прогиба, что гарантирует эффективную работу рукоятки при выполнении броска любого типа. Ее универсальность делает клюшку отличным выбором для юного хоккеиста. Крюк JS3 имеет легкий вес и обеспечивает более точный бросок и передачу. Длина производителя - 43 дюйма Полная длина клюшки - 124 см Длина клюшки: ☑20 FLEX – Длина 43 дюйма Особенности: ✅Уровень – Любительский ✅Точка прогиба – Гибридная ✅Технология крюка – JS3 Характеристики Возрастная группа YTH Линейка JETSPEED', '/root/images/Клюшка/CCM_JETSPEED 440 GRIP_YTH.png', null, 'JETSPEED 440 GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (424, 'JR', '', 'Описание Клюшка CCM RIBCOR 63K GRIP, любительская модель линейки RIBCOR 2018 года. Клюшки семейства RIBCOR разработана с целью придать каждому броску больше резкости. Точка прогиба у данной модели смещена в нижнюю часть, а крюк SXX2 имеет прогрессивно нарастающую жесткость от пятки к носку, для более резкого и точного броска. Длина производителя - 49 дюймов Полная длина клюшки - 140 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Нижняя ✅Технология крюка – SXX2 Характеристики Возрастная группа JR Линейка RIBCOR', '/root/images/Клюшка/CCM_RIBCOR 63K GRIP_JR.png', null, 'RIBCOR 63K GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (425, 'INT', '', 'Описание Клюшка BAUER NSX, любительская модель линейки NSX 2018 года. Клюшка выполнена из углеродного однонаправленного волокна. Волокна карбона в конструкции имеют повышенное натяжение и нити не переплетены между собой, а уложены в одном направлении. Данная модель имеет среднюю точку прогиба. Внешнее покрытие крюка выполнено из углеродного однонаправленного волокна. Полиуретановый сердечник крюка обеспечивает отличное чувство шайбы, уменьшает уровень вибрации при ударах и увеличивает прочность крюка. Конструкция клюшки двухсоставная. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Средняя ✅Технология трубы – Карбон UD ✅Технология крюка – Карбон UD, BRIDGECORE TECHNOLOGY ✅Конструкция – двухсоставная ✅Геометрия шафта – Квадратная Характеристики Возрастная группа INT', '/root/images/Клюшка/BAUER_NSX GRIPTAC_INT.png', null, 'NSX GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (426, 'SR', '', 'Описание Клюшка BAUER NSX, любительская модель линейки NSX 2018 года. Клюшка выполнена из углеродного однонаправленного волокна. Волокна карбона в конструкции имеют повышенное натяжение и нити не переплетены между собой, а уложены в одном направлении. Данная модель имеет среднюю точку прогиба. Внешнее покрытие крюка выполнено из углеродного однонаправленного волокна. Полиуретановый сердечник крюка обеспечивает отличное чувство шайбы, уменьшает уровень вибрации при ударах и увеличивает прочность крюка. Конструкция клюшки двухсоставная. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Средняя ✅Технология трубы – Карбон UD ✅Технология крюка – Карбон UD, BRIDGECORE TECHNOLOGY ✅Конструкция – двухсоставная ✅Геометрия шафта – Квадратная Характеристики Возрастная группа SR', '/root/images/Клюшка/BAUER_NSX GRIPTAC_SR.png', null, 'NSX GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (427, 'JR', '', 'Описание Клюшка BAUER VAPOR X2.5, любительская модель линейки VAPOR 2019 года. Клюшка выполнена из углеродного однонаправленного волокна. Волокна карбона в конструкции имеют повышенное натяжение и нити не переплетены между собой, а уложены в одном направлении. Геометрия трубы – округленные углы и вогнутые боковые стенки. Нижняя точка прогиба клюшки, обеспечивает более быстрый сход шайбы с крюка во время кистевых бросков. Внешнее покрытие крюка выполнено из углеродного однонаправленного волокна. Конструкция клюшки двухсоставная. Длина производителя - 52 дюймов Полная длина клюшки - 145 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Нижняя ✅Технология трубы – Карбон UD ✅Технология крюка – Карбон UD ✅Конструкция – двухсоставная ✅Геометрия шафта – С закругленными краями Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR X2.5 GRIPTAC_JR.png', null, 'VAPOR X2.5 GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (428, 'INT', '', 'Описание Клюшка CCM RIBCOR 63K GRIP, любительская модель линейки RIBCOR 2018 года. Клюшки семейства RIBCOR разработана с целью придать каждому броску больше резкости. Точка прогиба у данной модели смещена в нижнюю часть, а крюк SXX2 имеет прогрессивно нарастающую жесткость от пятки к носку, для более резкого и точного броска. Длина производителя - 57 дюймов Полная длина клюшки - 161 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Нижняя ✅Технология крюка – SXX2 Характеристики Возрастная группа INT Линейка RIBCOR', '/root/images/Клюшка/CCM_RIBCOR 63K GRIP_INT.png', null, 'RIBCOR 63K GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (429, 'YTH', '', 'Описание Клюшка BAUER VAPOR PRODIGY YTH, модель линейки VAPOR 2019 года. Детская клюшка VAPOR PRODIGY выполнена из однонаправленного углеродного волокна CARBON UD, благодаря этому клюшка имеет легкий вес и достаточно прочная. Клюшка имеет двухсоставную конструкцию. Нижняя точка прогиба, создана по технологии QUICK RELEASE TAPER, за счет этого броски получаются более резкие и точные. Крюк данной модели выполнен по технологии MaxBalance, за счет этого уменьшается вес клюшки. В качестве внутреннего сердечника крюка используется пена AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 46 дюймов Полная длина клюшки - 128 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Нижняя ✅Технология броска – QRT ✅Технология трубы – CARBON UD; Micro Feel II ✅Технология крюка – CARBON UD; MaxBalance; AERO FOAM I ✅Конструкция – Двухсоставная ✅Геометрия шафта – С закругленными краями ✅ YTH 20 FLEX – длина 46 дюймов Характеристики Возрастная группа YTH Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR PRODIGY_YTH.png', null, 'VAPOR PRODIGY', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (430, 'YTH', '', 'Описание Клюшка BAUER NEXUS 2N PRO, топовая клюшка линейки NEXUS. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX, которая увеличивает прочность и долговечность. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. В конструкции крюка используется углеродное волокно с плетением TeXtreme. Так же в данном крюке применена технология ADVANCED CARBON LAYERING, которая позволила уменьшить вес крюка и повысить долговечность. Длина производителя - 46 дюймов Полная длина клюшки - 129 см Особенности: • Уровень – Элитный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – TeXtreme; DURAFLEX(INT-SR) • Технология крюка - ACL; R-LITE; eLASTech(INT-SR) • Конструкция – MONOCOMP Характеристики Возрастная группа YTH Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS 2N PRO GRIPTAC_YTH.png', null, 'NEXUS 2N PRO GRIPTAC', 1, 13);
