INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (448, 'INT', '', 'Описание Клюшка BAUER VAPOR X2.7, полупрофессиональная модель линейки VAPOR 2019 года. Данная модель имеет нижнюю точку прогиба QRT, благодаря чему кистевые броски стали более резкими. Труба клюшки выполнена из углеродного волокна CARBON UD. Конструкция трубы MICRO FEEL II с двояковогнутыми боковыми стенками и скругленными углами, обеспечивает игроку максимальное удобство. Клюшка имеет двухсоставную конструкцию. Крюк клюшки выполнен по технологии MaxBalance, благодаря чему повышается прочность крюка. Внутренний сердечник имеет пену AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Нижняя • Технология броска – QRT • Технология трубы – CARBON UD; Micro Feel II • Технология крюка – CARBON UD; MaxBalance; AERO FOAM I • Конструкция – Двухсоставная • Геометрия шафта – С закругленными краями Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR X2.7 GRIPTAC_INT.png', null, 'VAPOR X2.7 GRIPTAC', 1, 13);
