INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (465, 'SR', '', 'Описание Клюшка BAUER SUPREME 2S, профессиональная клюшка в линейки SUPREME. Главным преимуществом клюшки является эффективность и прочность. Чтобы сделать эту клюшку самой прочной в серии SUPREME производитель укрепил стенки рукоятки. Клюшка выполнена из углеродного волокна 3К, благодаря этому клюшка очень прочна. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. Материал крюка углеродное волокно 12К.Крюк данной модели выполнен по технологии MaxBalance, за счет этого уменьшается вес клюшки. В качестве внутреннего сердечника крюка используется пена AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 64 дюйма Полная длина клюшки - 175 см Особенности: ✅Уровень – Профессиональный ✅Точка прогиба – Верхняя ✅Технология броска – MPK ✅Технология трубы – Карбон 3К ✅Технология крюка – Карбон 12К; MaxBalance; AERO FOAM I ✅Конструкция – High Performance mold ✅Геометрия шафта – Квадратная Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Клюшка/BAUER_SUPREME 2S GRIPTAC_SR.png', null, 'SUPREME 2S GRIPTAC', 1, 13);
