INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (480, 'INT', '', 'Описание Клюшка CCM SUPER TACKS AS2 PRO, топовая модель линейки TACKS 2019 года. Данная клюшка выполнена из углеродного волокна SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данное волокно имеет особое плетение, обеспечивая прочность и легкость клюшки. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Клюшка имеет среднюю точку прогиба, обеспечивая лучшую стабильность и контроль во время мощных бросков. Крюк ACU4 имеет легкую конструкцию и обеспечивает максимальный контроль и ощущение во время броска. Так же крюк имеет усиленную пятку и носок для максимальной прочности. ☑55 FLEX – Длина производителя - 55 дюймов; полная длина - 157 см ☑65 FLEX – Длина производителя - 57 дюймов; полная длина - 162 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Средняя ✅Технология трубы – X-FLOW ✅Технология крюка – ACU4 ✅Материал - SIGMATEX Характеристики Возрастная группа INT Линейка TACKS', '/root/images/Клюшка/CCM_SUPER TACKS AS2 PRO GRIP_INT.png', null, 'SUPER TACKS AS2 PRO GRIP', 2, 13);
