INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (482, 'JR', '', 'Описание Клюшка BAUER NEXUS 2N PRO, топовая клюшка линейки NEXUS. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX, которая увеличивает прочность и долговечность. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. В конструкции крюка используется углеродное волокно с плетением TeXtreme. Так же в данном крюке применена технология ADVANCED CARBON LAYERING, которая позволила уменьшить вес крюка и повысить долговечность. Длина производителя - 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: • Уровень – Элитный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – TeXtreme • Технология крюка - ACL; R-LITE • Конструкция – MONOCOMP Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS 2N PRO GRIPTAC_JR.png', null, 'NEXUS 2N PRO GRIPTAC', 1, 13);
