INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (577, 'INT', '', 'Описание Клюшка вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Данная клюшка представлена с крюком TECH STREAM, внутренняя часть которого выполнена из материала AERO FOAM 3 благодаря чему эта клюшка лучше поглощает удары и отличается меньшим весом в сравнении с предыдущей моделью этого поколения 1X. В расширенной части используется материал ADVANCED CARBON LAYERING и проходит воздушный канал, поэтому клюшка стала более легкой, при этом не потеряв своей прочности. А материал TeXtreme® в черенке клюшки обеспечивает превосходную передачу тактильных ощущений. Особенности: • Уровень – Элитный • Шафт – Композит • Крюк – TeXtreme®; AERO FOAM 3 • Расширенная часть – ACL Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Вратарская клюшка/BAUER_VAPOR 2X PRO_INT.png', null, 'VAPOR 2X PRO', 1, 14);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (578, 'SR', '', 'Описание Клюшка вратаря CCM EXTREME FLEX 4, топовая модель линейки EXTREME FLEX 2019 года. Широкая часть клюшки выполнена по технологии INNEGRA, благодаря этому клюшка имеет большую гибкость и хорошо поглощает вибрацию. Технология ZEROFLUX использована в широкой части, так же поглощает вибрацию и контролирует отскок шайбы. В черенке и крюке клюшки использован материал SIGMATEX, благодаря этому клюшка стала прочнее и легче. Так же черенок имеет двойную геометрию, а скругленные углы в клюшке повышают ее прочность и долговечность. Особенности: ✅Уровень – Элитный ✅ Шафт – SIGMATEX; Зона контроля GRIP; ✅Крюк – SIGMATEX ✅Расширенная часть – INNEGRA; ZEROFLUX Характеристики Возрастная группа SR Линейка EXTREME FLEX', '/root/images/Вратарская клюшка/CCM_EXTREME FLEX 4_SR.png', null, 'EXTREME FLEX 4', 2, 14);
