INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (580, 'SR', '', 'Описание Клюшка вратаря BAUER SUPREME ULTRASONIC, топовая модель линейки SUPREME 2020 года. Нижняя часть клюшки и крюк выполнены из материала TeXtreme с добавления ADVANCED CARBON LAYERING, благодаря чему клюшка стала более долговечной. Верхняя часть клюшки выполнена из углеродного волокна 3K, который увеличивает долговечность и снижает вес, при этом улучшая баланс. В качестве внутреннего сердечника крюка используется пена AERO FOAM 3. Технология ERGO SPINE характеризуется трехмерной формой, благодаря которой клюшка является самой легкой и сбалансированной на рынке в данный момент. Особенности: ✅ Уровень – Элитный ✅ Шафт – карбон 3К; ✅ Крюк – AERO FOAM 3; TeXtreme ✅ Расширенная часть – ERGO SPINE; TeXtreme; ACL Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Вратарская клюшка/BAUER_SUPREME ULTRASONIC_SR.png', null, 'SUPREME ULTRASONIC', 1, 14);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (581, 'SR', '', 'Описание Клюшка вратаря CCM AXIS PRO, топовая модель линейки AXIS 2020 года. Клюшка AXIS PRO – это уменьшенный вес, увеличенная долговечность и исключительная производительность. Новая обтекаемая конструкция клюшки помогает избавиться от лишнего веса, при этом увеличивая производительность. Шафт клюшки выполнен из комбинации высококачественных углеродных волокон NANOLITE и SEGMATEX. Широкая часть клюшки выполнена по технологии ZEROFLUX, благодаря этому клюшка имеет большую гибкость и хорошо поглощает вибрацию. Крюк клюшки выполнен по технологии PEEL-PLY, которая помогает снизить вес и увеличивает контроль над шайбой. Так же конструкция всей клюшки выполнена с использованием технологии — SILVER SPREAD TOW-это инновационный процесс плетения углеродного волокна, обеспечивающий максимальную прочность и долговечность клюшки, а также уменьшая ее вес. Особенности: ✅Уровень – Элитный ✅ Шафт – NANOLITE; Зона контроля GRIP ✅Крюк – NANOLITE; PEEL-PLY ✅Расширенная часть – NANOLITE; ZEROFLUX Характеристики Возрастная группа SR Линейка AXIS', '/root/images/Вратарская клюшка/CCM_AXIS PRO_SR.png', null, 'AXIS PRO', 2, 14);
