INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (650, 'YTH', '', 'Описание Наплечник вратаря BAUER GSX, любительская модель 2020 года. Представляем вашему вниманию абсолютно новый наплечник GSX. Защитные элементы наплечника выполнены из пены средней плотности. Конструкция DYNAMIC 3 FLEX, использованная в рукавах, придает наплечнику большую гибкость. Так же в наплечнике использована система регулировки длины рукава и торсовой части, которая придает наплечнику лучшую мобильность и максимальный комфорт. Внутренняя подкладка выполнена с применением технологии THERMO MAX, которая отводит влагу Особенности: ✅Уровень – Любительская ✅Регулировка – Два эластичных ремня в области спины; Рукава; Плечи ✅Защита плеч – Пена средней плотности ✅Защита рук – Пена средней плотности; DYNAMIC 3 FLEX ✅Защита груди – Пена средней плотности ✅Защита ребер – Пена средней плотности ✅Подкладка – THERMO MAX Характеристики Возрастная группа YTH', '/root/images/Нагрудник вратаря/BAUER_GSX PRODIGY_YTH.png', null, 'GSX PRODIGY', 1, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (651, 'YTH', '', 'Описание Наплечник вратаря CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Конструкция защиты наплечника была полностью изменена, теперь он больше охватывает тело, больше подвижен, чем предыдущая коллекция. Так же появилась возможность регулировки в области плеч для более оптимальной посадки. Все защитные элементы наплечника выполнены из полиэтилена, благодаря этому наплечник имеет хорошие защитные свойства и легкий вес. Особенности: ✅Уровень – Детский, профессиональный ✅Защита плеч – Полиэтилен ✅Защита рук – Полиэтилен ✅Защита груди – Полиэтилен ✅Защита ребер – Полиэтилен Характеристики Возрастная группа YTH', '/root/images/Нагрудник вратаря/CCM_YFLEX 2_YTH.png', null, 'YFLEX 2', 2, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (652, 'JR', '', 'Описание Наплечник вратаря BAUER SUPREME S27, любительская модель линейки BAUER 2018 года. Защиту наплечника усилили на 30% относительно прошлой линейки SUPREME. Для улучшения защиты добавили вставки из пены высокой плотности в область плеч, живота и рук. Также добавили возможность регулировки рук по длине на пол дюйма, чтобы обеспечить максимальный комфорт во время движения. Особенности: • Уровень – Любительский • Регулировка – Два эластичных ремня в области спины • Защита плеч – Пена высокой плотности • Защита рук – Пена высокой плотности • Защита груди – Пена высокой плотности • Защита ребер – Пена высокой плотности • Подкладка –Влагоотталкивающий материал Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Нагрудник вратаря/BAUER_SUPREME S27_JR.png', null, 'SUPREME S27', 1, 20);
