INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (654, 'JR', '', 'Описание Наплечник вратаря CCM PREMIER R1.5, юниорская профессиональная модель линейки PREMIER 2019 года. Данная модель предназначена для вратарей, которые хотят оставить, как можно меньше места в воротах. Производитель создал квадратные рукава, чтобы облегчить движения рук вдоль тела, а также оптимизировал ширину, чтобы обеспечить максимальную зону охвата. Область рук и груди выполнена из высококачественного полиэтилена, который обеспечивает отличный уровень защиты. Для защиты плеч используется пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Область живота имеет сегментированные вставки из высококачественного полиэтилена, которые обеспечивают максимальную подвижность и хорошие защитные свойства. Система регулировки наплечника позволяет Вам, максимально комфорта настроить его под себя. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Полиэтилен; JDP ✅Защита плеч – Пена высокой плотности ✅Защита живота – Сегментированные вставки из полиэтилена Характеристики Возрастная группа JR Линейка PREMIER', '/root/images/Нагрудник вратаря/CCM_PREMIER R1.5 LE_JR.png', null, 'PREMIER R1.5 LE', 2, 20);
