INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (660, 'JR', '', 'Описание Наплечник вратаря CCM AXIS 1.5, юниорская полупрофессиональная модель линейки AXIS 2020 года. Представляем Вашему вниманию новую линейку AXIS. Главное преимущество этой линейки максимальный охват тела, гибкость и комфорт. Материал наплечника выполнен из нейлона и высококачественной сетки, обеспечивая долговечность, комфорт и высокую воздухопроницаемость. Для большей защиты в области груди используется пена из формованного полиэтилен, а для защиты живота и ребер используется пена высокой плотности. Сочетание этих пен идеально подходит для легкого ощущения и оптимальной защиты. В новой линейке AXIS полностью изменена конструкция плеча, теперь она имеет квадратную форму и выполнена из пены высокой плотности. Так же они полностью сегментированы и имеют регулировку с помощью липучки для идеальной посадки. Рукава так же выполнены из пены высокой плотности и обеспечивают превосходную защиту и мобильность. Особенности: ✅Уровень – Полупрофессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Пена высокой плотности ✅Защита ребер – Пена высокой плотности ✅Защита плеч – Пена высокой плотности ✅Защита живота – Пена высокой плотности Характеристики Возрастная группа JR Линейка AXIS', '/root/images/Нагрудник вратаря/CCM_AXIS 1.5_JR.png', null, 'AXIS 1.5', 2, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (661, 'JR', '', 'Описание Наплечник вратаря CCM EXTREME FLEX SHIELD E2.5, юниорская профессиональная модель линейки EXTREME FLEX 2018 года. Наплечник обладает максимальной мобильностью и отличными защитными функциям. Все защитные элементы наплечника выполнены из высококачественного полиэтилена, благодаря этому наплечник имеет легкий вес и подвижность при максимальной защите. Данная модель имеет систему регулировки VELCO ADJUSTMENT, благодаря чему наплечник можно максимально подогнать по телу. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Полиэтилен ✅Защита плеч – Полиэтилен ✅Защита живота – Сегментированная конструкция из полиэтилена Характеристики Возрастная группа JR Линейка EXTREME FLEX', '/root/images/Нагрудник вратаря/CCM_EXTREME FLEX SHIELD E2.5_JR.png', null, 'EXTREME FLEX SHIELD E2.5', 2, 20);
