INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (665, 'INT', '', 'Описание Наплечник вратаря CCM EXTREME FLEX SHIELD E2.9, профессиональная модель линейки EXTREME FLEX 2018 года. Наплечник обладает максимальной мобильностью и отличными защитными функциям. В области груди для большей защиты используется пена высокой плотности. Плечевые чаши выполнены из формованной пены высокой плотности, обеспечивая максимальный уровень защиты. Подвижные защитные элементы в области ключицы и плеча выполнено из комбинации пен разной плотности. В рукавах защитные элементы выполнены из формованной пены высокой плотности, обеспечивая максимальную гибкость и хороший уровень защиты. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Пена высокой плотности ✅Защита рук и локтя – Пена высокой плотности ✅Защита плеч – Пена высокой плотности ✅Защита живота – Сегментированная пена высокой плотности Характеристики Возрастная группа INT Линейка EXTREME FLEX', '/root/images/Нагрудник вратаря/CCM_EXTREME FLEX SHIELD E2.9_INT.png', null, 'EXTREME FLEX SHIELD E2.9', 2, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (666, 'INT', '', 'Описание Наплечник вратаря BAUER ELITE, полупрофессиональная модель 2020 года. Представляем вашему вниманию абсолютно новый наплечник ELITE. В данной ценовой категории теперь не будет разделения на линейки SUPREME и VAPOR. Данная модель объединяет в себе мобильность от VAPOR и защитные свойства от SUPREME. Защитные элементы наплечника выполнены из высококачественной пены SHOCKLITE. Конструкция DYNAMIC 3 FLEX, использованная в рукавах, придает наплечнику большую гибкость. Так же в наплечнике использована система регулировки длины рукава и торсовой части, которая придает наплечнику лучшую мобильность и максимальный комфорт. Внутренняя подкладка выполнена с применением технологии THERMO MAX+, которая эффективно отводит влагу Особенности: ✅Уровень – Полупрофессиональный ✅Регулировка – Два эластичных ремня в области спины; Рукава; Плечи ✅Защита плеч – SHOCKLITE ✅Защита рук – SHOCKLITE; DYNAMIC 3 FLEX ✅Защита груди – SHOCKLITE ✅Защита ребер – SHOCKLITE ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа INT', '/root/images/Нагрудник вратаря/BAUER_ELITE_INT.png', null, 'ELITE', 1, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (667, 'SR', '', 'Описание Наплечник вратаря BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. За счет уменьшения плечевых чашек, наплечник получился очень мобильным. В области плеча использована пена POWERLITE, который представляет собой смесь вспененного пропилена, пены высокой плотности и композитного материала CURV®, обеспечивающий защитные свойства. Защита локтя выполнена из пены PORON XRD для лучшего поглощения удара. Так же эта пена использована во всей области защиты рук. Внутренняя подкладка 37,5®, которая обработана по технологии SANITIZED®, для предотвращения развития бактерий и возникновения неприятного запаха. Особенности: • Уровень – Элитный • Регулировка – Два эластичных ремня в области спины • Защита плеч – POWERLITE • Защита рук – PORON XRD; POWERLITE • Защита груди – VENT ARMOR • Защита ребер – Пена высокой плотности • Подкладка – 37,5®; SANITIZED® Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник вратаря/BAUER_SUPREME 2S PRO_SR.png', null, 'SUPREME 2S PRO', 1, 20);
