INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (671, 'SR', '', 'Описание Наплечник вратаря BAUER SUPREME ULTRASONIC, топовая модель линейки SUPREME 2020 года. За счет уменьшения плечевых чашек, наплечник получился очень мобильным. В области плеча использована пена SHOCKLITE, которая обеспечивает наплечнику максимальную защиту при минимальном весе и композитный материал CURV®, так же обеспечивающий защитные свойства. Рукава выполнены из высокотехнологичной пены SHOCKLITE PRO, обеспечивающая лучшую мобильность. Область грудины выполнена из смеси высокотехнологичных пен SHOCKLITE и PORON XRD, которая максимально поглощает энергию удара. Внутренняя подкладка выполнена с применением технологии THERMO CORE, которая эффективно отводит влагу, предотвращает рост бактерий и возникновение неприятного запаха. Наплечник имеет анатомическую посадку. Особенности: ✅Уровень – Элитный ✅Регулировка – Два эластичных ремня в области спины; Рукава; Плечи ✅Защита плеч – SHOCKLITE; CURV ✅Защита рук – SHOCKLITE PRO ✅Защита груди – SHOCKLITE; PORON XRD ✅Защита ребер – Пена высокой плотности ✅Подкладка – THERMO CORE Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник вратаря/BAUER_SUPREME ULTRASONIC_SR.png', null, 'SUPREME ULTRASONIC', 1, 20);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (672, 'SR', '', 'Описание Наплечник вратаря CCM AXIS PRO, топовая модель линейки AXIS 2020 года. Представляем Вашему вниманию новую линейку AXIS. Главное преимущество этой линейки максимальный охват тела, гибкость и комфорт. Материал наплечника выполнен из нейлона и высококачественной сетки, обеспечивая долговечность, комфорт и высокую воздухопроницаемость. Для максимальной защиты в области груди и ребер используется пена D3O, которая поглощает энергию удара. В новой линейке AXIS полностью изменена конструкция плеча, теперь она имеет квадратную форму и выполнена из пены высокой плотности. Так же они полностью сегментированы и имеют регулировку с помощью липучки для идеальной посадки. Рукава так же выполнены из пены высокой плотности. Особенности: ✅Уровень – Элитный ✅Защита груди – D3O ✅Защита рук и локтя – Пена высокой плотности ✅Защита ребер – D3O ✅Защита плеч – Пена высокой плотности ✅Защита живота – Сегментированная пена высокой плотности Характеристики Возрастная группа SR Линейка AXIS', '/root/images/Нагрудник вратаря/CCM_AXIS_SR.png', null, 'AXIS', 2, 20);
