INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (678, 'YTH', '', 'Описание Трусы вратаря CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Конструкция трусов была изменена по сравнению с предыдущей коллекцией, теперь они более плотнее сидят в бедрах и обеспечивают необходимую гибкость. Внешний материал трусов выполнен из нейлона плотностью 200 ден с эластичными вставками для большего комфорта. Область позвоночника выполнена из сегментированной пены для максимального удобства. Все защитные элементы трусов выполнены из полиэтилена, благодаря этому наплечник имеет хорошие защитные свойства и легкий вес. Особенности: ✅Уровень – Детский, профессиональный ✅Защита бедра – Полиэтилен ✅Защита спины – Сегментированная пена ✅Чехол – Нейлон плотностью 200 ден Характеристики Возрастная группа YTH', '/root/images/Трусы вратаря/CCM_YFLEX 2_YTH.png', null, 'YFLEX 2', 2, 21);
