INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (680, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Любительский • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – MD FOAM • Защита спины – Пена • Подкладка – Влагоотталкивающая Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME S27_SR.png', null, 'SUPREME S27', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (681, 'JR', '', 'Описание Трусы вратаря CCM PREMIER R1.5 LE, юниорская профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Все защитные элементы выполнены из высококачественного полиэтилена. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа JR Линейка PREMIER', '/root/images/Трусы вратаря/CCM_PREMIER R1.5 LE_JR.png', null, 'PREMIER R1.5 LE', 2, 21);
