INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (685, 'INT', '', 'Описание Трусы вратаря BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. Особенности: ✅Уровень – Полупрофессиональный ✅Защита бедра- Пена высокой плотности; ✅Защита спины- FREE FLEX; DINAMIC 2.FLEX ✅Подкладка – THERMO MAX Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Трусы вратаря/BAUER_VAPOR X2.9_INT.png', null, 'VAPOR X2.9', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (686, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Защита тазобедренных суставов имеет плавающую конструкцию и выполнена с использованием пены PORON XRD, которая эффективно поглощает энергию удара, создавая отличную защиту. Набедренные щитки обладают отличными защитными свойствами, точно повторяют анатомические контуры тела, обеспечивая комфортную посадку и плотное прилегание элементов защиты. Внутренняя подкладка выполнена из материала THERMO MAX. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Полупрофессиональный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – PORON XRD • Защита спины – Пена • Подкладка – THERMO MAX Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME S29_SR.png', null, 'SUPREME S29', 1, 21);
