INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (689, 'JR', '', 'Описание Трусы вратаря CCM AXIS 1.5,юниорская полупрофессиональная модель линейки AXIS 2020 года. Представляем Вашему вниманию трусы новой линейки AXIS. Данная модель имеет свободную посадку для большей мобильности. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Область тазобедренных суставов и бедра для максимальной защиты выполнена из пластика и полиэтилена. Благодаря этим материалам трусы имеют не большой вес и хорошую подвижность. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Чехол трусов выполнен из высокопрочного нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Полупрофессиональный ✅Защита бедра – Полиэтилен; Пластик ✅Защита тазобедренная – Полиэтилен; Пластик ✅Защита позвоночника - Полиэтилен ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Высокопрочный нейлон 400 ден Характеристики Возрастная группа JR Линейка AXIS', '/root/images/Трусы вратаря/CCM_AXIS 1.5_JR.png', null, 'AXIS 1.5', 2, 21);
