INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (691, 'INT', '', 'Описание Трусы вратаря BAUER ELITE, полупрофессиональная модель 2020 года. В трусах с нового сезона не будет разделения на VAPOR и SUPREME. Данная модель имеет очень хорошие защитные свойства и отличную мобильность. Защитные элементы трусов выполнены из высококачественной пены SHOCKLITE. Также трусы оснащены системой EXTENSION FLEX, благодаря которой шорты можно удлинить на 1 дюйм(2.5см). Внешний материал трусов выполнен из износостойкого нейлона плотностью 840 ден. В качестве внутренней подкладки используется THERMOMAX+, который максимально отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Регулировка – Система удлинения на 1 дюйм (2.5 см) ✅Защита бедра – SHOCKLITE ✅Защита тазобедренная – SHOCKLITE ✅Подкладка – THERMOMAX+ Характеристики Возрастная группа INT', '/root/images/Трусы вратаря/BAUER_ELITE_INT.png', null, 'ELITE', 1, 21);
