INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (694, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME 2S, топовая модель линейки SUPREME 2018 года. Для защиты бедер использована пена POWERLITE, это смесь пены высокой плотности, пены EPP и композитного материала CURV, обеспечивающая вратарю надежную защиту. В области таза использован материал XRD для того чтоб вратарь был всегда защищен во время перемещений. Внутри трусов находится специальный пояс, используя который вратарь может наиболее удобным для себя образом отрегулировать эти трусы в области талии. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Внутренняя подкладка трусов 37,5, которая обработана по технологии SANITIZED. Особенности: • Уровень – Элитный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – POWERLITE • Защита спины – POWERLITE • Подкладка – 37,5; SANITIZED Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы вратаря/BAUER_SUPREME 2S PRO_SR.png', null, 'SUPREME 2S PRO', 1, 21);
