INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (697, 'SR', '', 'Описание Трусы вратаря CCM PREMIER PRO LE, топовая модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Для защиты бедра используется формованный пластик с пеной низкой плотности. Для максимальной защиты в области тазобедренных суставов используется материал D3O, который поглощает энергию удара. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Чехол трусов выполнен из высокопрочного абразивного нейлона плотностью 420 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Элитный ✅Защита бедра – Пластик; Пена низкой плотности ✅Защита тазобедренная – D3O ✅Защита позвоночника - EVA ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Высокопрочный абразивный нейлон 420 ден Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Трусы вратаря/CCM_PREMIER PRO LE_SR.png', null, 'PREMIER PRO LE', 2, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (698, 'SR', '', 'Описание Трусы вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Также трусы оснащены системой EXTENSION FLEX, которая представляет собой застежку-молнию, посредством которой шорты можно удлинить на 1 дюйм(2.5см). Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. В области бедра используется композитный материал CURV, обеспечивающий повышенную прочность и надежную защиту. Особенности: ✅Уровень – Элитный ✅Регулировка – Система удлинения на 1 дюйм (2.5 см) ✅Защита бедра- CURV ✅Защита спины- FREE FLEX; AEROLITE ✅Подкладка – THERMOCORE Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Трусы вратаря/BAUER_VAPOR 2X PRO_SR.png', null, 'VAPOR 2X PRO', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (699, 'SR', '', 'Описание Трусы вратаря BAUER PRO, топовая модель 2020 года. В трусах с нового сезона не будет разделения на VAPOR и SUPREME. Данная модель имеет очень хорошие защитные свойства и отличную мобильность. Композитный материал CURV, который используется в области бедра и тазобедренных суставах обеспечивает максимальную защиту при минимальном весе. Так же для большей защиты в области тазобедренных суставов используется новая пена SHOCKLITE. Также трусы оснащены системой EXTENSION FLEX, благодаря которой шорты можно удлинить на 1 дюйм(2.5см). Внешний материал трусов выполнен из износостойкого нейлона плотностью 840 ден. В качестве внутренней подкладки используется THERMOCORE, который максимально отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Элитный ✅Регулировка – Система удлинения на 1 дюйм (2.5 см) ✅Защита бедра- CURV ✅Защита тазобедренная – CURV; SHOCKLITE ✅Подкладка – THERMOCORE Характеристики Возрастная группа SR', '/root/images/Трусы вратаря/BAUER_PRO_SR.png', null, 'PRO', 1, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (700, 'SR', '', 'Описание Трусы вратаря CCM AXIS PRO, топовая модель линейки AXIS 2020 года. Представляем Вашему вниманию трусы новой линейки AXIS. Данная модель имеет свободную посадку для большей мобильности. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Для защиты тазобедренных суставов используется пена высокой плотности в сочетании с вспененным полиэтиленом. Для максимальной защиты в области тазобедренных бедра используется высокотехнологичная пена D3O, который поглощает энергию удара, пена низкой плотности и пластик. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Чехол трусов выполнен из высокопрочного абразивного нейлона плотностью 420 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Элитный ✅Защита бедра – D3O; Пена низкой плотности; Пластик ✅Защита тазобедренная – Вспененный полиэтилен; Пена высокой плотности ✅Защита позвоночника - EVA ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Высокопрочный абразивный нейлон 420 ден Характеристики Возрастная группа SR Линейка AXIS', '/root/images/Трусы вратаря/CCM_AXIS_SR.png', null, 'AXIS', 2, 21);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (701, 'JR', '', 'Описание Коньки вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Коньки оснащены стаканами VERTEXX EDGE и лезвиями LS 1G, которые обеспечивают вратарю больший угол атаки. Носок данной модели усилен, что делает их более защищенными. Внешний материал ботинка изготовлен из материала TEXPLATE , за счет чего они более жесткие и устойчивые, но не такие жесткие как топовые коньки. У коньков ассиметричная форма ботинка. Подошва выполнена из облегченной композитной конструкции TPR. Особенности: • Уровень – Любительский • Внешний материал – TEXPLATE • Язык – Ассиметричный войлочный язык • Подошва – TPR • Стакан – VERTEXX EDGE • Лезвие – LS 1G • Петля LUNDQVUST – Есть • Подкладка – Влагоотталкивающий материал Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Коньки вратаря/BAUER_SUPREME S27_JR.png', null, 'SUPREME S27', 1, 22);
