INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (704, 'SR', '', 'Описание Коньки вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Коньки оснащены стаканами VERTEXX EDGE и лезвиями LS 1G, которые обеспечивают вратарю больший угол атаки. Носок данной модели усилен, что делает их более защищенными. Внешний материал ботинка изготовлен из материала TEXPLATE , за счет чего они более жесткие и устойчивые, но не такие жесткие как топовые коньки. У коньков ассиметричная форма ботинка. Подошва выполнена из облегченной композитной конструкции TPR. Особенности: • Уровень – Любительский • Внешний материал – TEXPLATE • Язык – Ассиметричный войлочный язык • Подошва – TPR • Стакан – VERTEXX EDGE • Лезвие – LS 1G • Петля LUNDQVUST – Есть • Подкладка – Влагоотталкивающий материал Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Коньки вратаря/BAUER_SUPREME S27_SR.png', null, 'SUPREME S27', 1, 22);
