INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (719, 'YTH', '', 'Описание Щитки вратаря BAUER GSX PRODIGY, детская модель 2020 года. Абсолютно переработанная детская модель щитков GSX. В сравнении с предыдущей модель PRODIGY 2.0 конструкция щитков стала заметно мягче, обеспечив максимальный комфорт во время передвижения для маленьких вратарей. Система фиксации щитков с помощью эластичных ремней-липучек. Область коленной чашки имеет регулировки при помощи липучки. Внешний материал щитков высококачественная искусственная кожа. Основные особенности: ✅Уровень – Детский, начинающий ✅Конструкция – Мягкая ✅Материал – Искусственная кожа ✅Ремни – Эластичные, с липучкой Характеристики Возрастная группа YTH', '/root/images/Щитки вратаря/BAUER_GSX PRODIGY_YTH.png', null, 'GSX PRODIGY', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (720, 'YTH', '', 'Описание Щитки вратаря CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Щитки подходят для начинающих вратарей. Данная модель очень мобильна и удобна. Внешний материал щитков SPEEDSKIN, который обеспечивает максимальное скольжения. Щиток имеет хорошую гибкую конструкцию с одним изломом. С обратной стороны расположена быстросъемная система ремней. Колено в данной модели регулируемое, благодаря этому вы можете брать щитки на вырост. Особенности: ✅Возраст – Дети (4-7 лет) ✅Конструкция – Мягкая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа YTH', '/root/images/Щитки вратаря/CCM_YFLEX 2_YTH.png', null, 'YFLEX 2', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (721, 'JR', '', 'Описание Щитки вратаря BAUER GX, любительская модель 2020 года. Щитки выполнены из прочной синтетической кожи. Конструкция щитка выполнена с двумя изломами под коленом и над коленом. Щитки оснащены дополнительной защитой бедра, которая обеспечивает лучшую защиту верхней части колена вратаря, также ее можно легко отрегулировать или убрать. Щитки имеют систему креплений TUNE FIT. Основные особенности: ✅Уровень – Любительский ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – Синтетическая кожа ✅Ремни – TUNE FIT Характеристики Возрастная группа JR', '/root/images/Щитки вратаря/BAUER_GX_JR.png', null, 'GX', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (722, 'JR', '', 'Описание Щитки BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Данные модель имеет конструкцию DINAMIC FLEX и ботинок с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Любительский ✅Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – Модернизированный синтетический ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Щитки вратаря/BAUER_VAPOR X2.7_JR.png', null, 'VAPOR X2.7', 1, 23);
