INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (728, 'INT', '', 'Описание Щитки BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Щитки имеют более жесткую конструкцию с одним изломом под коленом, чем предыдущая коллекция, благодаря чему повысилась прочность и долговечность. Так же щитки имеют систему регулировки CRS для лучшего удобства. Пластины в области колена и голени выполнены из материала C.O.R.T.E.C.H. для увеличения скольжения. Крепление на носке смещено, что обеспечивает вратарю улучшенный угол атаки. Внутренняя подкладка из влагоотталкивающего нейлона. Особенности: • Уровень – Полупрофессиональный • Конструкция – Жесткая, с одним изломом под коленом • Материал – Искусственная кожа; C.O.R.T.E.C.H. в области колена и голени • Ремни – CRS • Колено – Конструкция AKL • Голень – Пена VENT ARMOR Характеристики Возрастная группа INT Линейка SUPREME', '/root/images/Щитки вратаря/BAUER_SUPREME S29_INT.png', null, 'SUPREME S29', 1, 23);
