INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (730, 'SR', '', 'Описание Щитки вратаря CCM EXTREME FLEX E4.5, модель линейки EXTREME FLEX 2019 года. Данная модель отличается хорошим уровнем защиты, гибкостью и производительностью. Внешний материал щитков выполнен из синтетического материала SPEEDSKIN. Этот материал обеспечивает максимально быстрое скольжения во время игры. Двойная легкая сердцевина создает ощущение гибкости под коленом и жесткости над ним, обеспечивая устойчивость и защиту. На обратной стороне вы получаете систему ремешков, обеспечивающую быстроту движений. Она будет удерживать ногу и придавать щитку плотное прилегание. Наколенник, выполнен по технологии трехмерного литья и обеспечивает эффективную амортизацию и дополнительное удобство. Особенности: ✅Уровень – Любительский ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка EXTREME FLEX', '/root/images/Щитки вратаря/CCM_EXTREME FLEX E4.5_SR.png', null, 'EXTREME FLEX E4.5', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (731, 'INT', '', 'Описание Щитки BAUER SUPREME 3S, полупрофессиональная модель линейки SUPREME 2020 года. Как и модель ULTRASONIC, данные щитки оснащены новой конструкцией SHOCKLITE 151, которая позволяет максимально точно управлять отскоком шайбы. Нижняя часть щитка имеет подвижную конструкцию CONNECT FLEX 125, которая обеспечивает комфорт при передвижении. На скользящей стороне щитка используется материал CURV и CORTECH ST для быстроты перемещения вратаря по льду. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. Основные особенности: ✅Уровень – Полупрофессиональный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – C.O.R.T.E.C.H ST ✅Ремни – TUNE FIT Характеристики Возрастная группа INT Линейка SUPREME', '/root/images/Щитки вратаря/BAUER_SUPREME 3S_INT.png', null, 'SUPREME 3S', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (732, 'INT', '', 'Описание Щитки BAUER VAPOR 2X, полупрофессиональная модель линейки VAPOR 2019 года. Щиток данной модели потерпел колоссальные изменения по сравнению с щитками предыдущей коллекции VAPOR X900. Теперь они оснащены конструкцией DINAMIC FLEX и ботинком с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. В области бедра используется композитный материал CURV, благодаря этому щиток имеет очень легкий вес и стал более прочным и гибким. В области колена и коленного блока используется материал C.O.R.T.E.C.H ST, которое обеспечивает лучшее скольжение по сравнению с обычной искусственной кожей. Для защиты икроножной мышцы используется пена AEROLITE. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Полупрофессиональный ✅ Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – C.O.R.T.E.C.H ST; Искусственная кожа ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа INT Линейка VAPOR', '/root/images/Щитки вратаря/BAUER_VAPOR 2X_INT.png', null, 'VAPOR 2X', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (733, 'SR', '', 'Описание Щитки BAUER SUPREME 3S, полупрофессиональная модель линейки SUPREME 2020 года. Как и модель ULTRASONIC, данные щитки оснащены новой конструкцией SHOCKLITE 151, которая позволяет максимально точно управлять отскоком шайбы. Нижняя часть щитка имеет подвижную конструкцию CONNECT FLEX 125, которая обеспечивает комфорт при передвижении. На скользящей стороне щитка используется материал CURV и CORTECH ST для быстроты перемещения вратаря по льду. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. Основные особенности: ✅Уровень – Полупрофессиональный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – C.O.R.T.E.C.H ST ✅Ремни – TUNE FIT Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Щитки вратаря/BAUER_SUPREME 3S_SR.png', null, 'SUPREME 3S', 1, 23);
