INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (735, 'SR', '', 'Описание Щитки вратаря CCM AXIS 1.9, полупрофессиональная модель линейки AXIS 2020 года. CCM произвел революцию в своей флагманской линейке и выпустил абсолютно новые щитки линейки AXIS, которые стали гораздо легче предыдущей модели и более подвижной. Легкий вес стал благодаря абсолютно новой технологии LiteXCore, которая используется в качестве внутреннего наполнения. В качестве внутреннего верхнего слоя используется пены высокой и средней плотности, которая в сочетании с технологией LiteXCore обеспечивает хорошие защитные свойства и лучший отскок шайбы. Внешний материал щитков выполнен из материала SPEEDSKIN, который обеспечивает лучшую долговечность. Нижняя часть щитка имеет конструкцию на 90 градусов, обеспечивая лучшую гибкость во время мощных толчков и расширенный диапазон движения. С обратной стороны щитки оснащены эластичными ремешками STRAP SYSTEM, благодаря чему вратарю проще садиться в бабочку и вставать на ноги. Особенности: ✅Уровень – Полупрофессиональный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка AXIS', '/root/images/Щитки вратаря/CCM_AXIS 1.9_SR.png', null, 'AXIS 1.9', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (736, 'SR', '', 'Описание Щитки вратаря CCM EXTREME FLEX E4.9, полупрофессиональная модель линейки EXTREME FLEX 2019 года. Данная модель отличается высоким уровнем защиты, максимальной гибкостью и производительностью. Внешний материал щитков выполнен из высокопрочного синтетического материала SPEEDSKIN. Этот материал обеспечивает максимально быстрое скольжения во время игры. Двойная легкая сердцевина создает ощущение гибкости под коленом и жесткости над ним, обеспечивая устойчивость и защиту. На обратной стороне вы получаете систему ремешков, обеспечивающую быстроту движений. Она будет удерживать ногу и придавать щитку плотное прилегание. Наколенник, выполнен по технологии трехмерного литья и обеспечивает эффективную амортизацию и дополнительное удобство. Особенности: ✅Уровень – Профессиональный ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка EXTREME FLEX', '/root/images/Щитки вратаря/CCM_EXTREME FLEX E4.9_SR.png', null, 'EXTREME FLEX E4.9', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (737, 'SR', '', 'Описание Щитки вратаря CCM PREMIER II PRO SR, топовая модель линейки PREMIER 2018 года. Внешний материал щитков SPEEDSKIN, который лучше скользит по льду. Внутренняя конструкция щитков выполнена по новой технологии, благодаря этому они весят на 20% меньше, чем предыдущая коллекция. В основу этих щитков положена технология MAX REBOUND (максимальный отскок). По сути это вспененный материал, при попадании в который шайба отскакивает далеко в поле. Так же в этих щитках изменена нижняя часть, закрывающая конек, теперь вратарю будет проще отталкиваться ото льда, стоя на коленях. С обратной стороны щитки оснащены эластичными ремешками STRAP SYSTEM, благодаря чему вратарю проще садиться в бабочку и вставать на ноги. Особенности: ✅Уровень – Элитный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Щитки вратаря/CCM_PREMIER II PRO_SR.png', null, 'PREMIER II PRO', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (738, 'SR', '', '', '/root/images/Щитки вратаря/CCM_EXTREME FLEX III PRO_SR.png', null, 'EXTREME FLEX III PRO', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (739, 'SR', '', 'Описание Щитки BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. Внешняя часть щитков выполнена из материала C.O.R.T.E.C.H., благодаря ему улучшено скольжение, повышена прочность. Если сравнивать с 1S, то щитки стали более гибкими. Для повышения прочности щитков в качестве их основы использован материал POWERLITE, этот материал представляет собой смесь пены EPP и композитного материала CURV. Щитки имеют более жесткую конструкцию с одним изломом под коленом, чем предыдущая коллекция, благодаря чему повысилась прочность и долговечность. Так же щитки имеют систему регулировки CRS для лучшего удобства. Особенности: • Уровень – Элитный • Конструкция – Жесткая, с одним изломом под коленом • Материал –C.O.R.T.E.C.H. • Ремни – CRS • Колено – Конструкция NASH • Голень – Пена VENT ARMOR; 37,5 Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Щитки вратаря/BAUER_SUPREME 2S PRO_SR.png', null, 'SUPREME 2S PRO', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (740, 'SR', '', 'Описание Щитки вратаря CCM AXIS, топовая модель линейки AXIS 2020 года. CCM произвел революцию в своей флагманской линейке и выпустил абсолютно новые щитки линейки AXIS, которые стали гораздо легче предыдущей модели и более подвижней. Легкий вес стал благодаря абсолютно новой технологии LiteXCore, которая используется в качестве внутреннего наполнения. В качестве внутреннего верхнего слоя используется технология MAX REBOUND+, которая в сочетании с технологией LiteXCore обеспечивает лучший отскок шайбы. Внешний материал щитков выполнен из уже проверенного материала SPEEDSKIN, который обеспечивает лучшую долговечность. Нижняя часть щитка имеет конструкцию на 90 градусов, обеспечивая лучшую гибкость во время мощных толчков и расширенный диапазон движения. Особенности: ✅Уровень – Элитный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка AXIS', '/root/images/Щитки вратаря/CCM_AXIS_SR.png', null, 'AXIS', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (741, 'SR', '', 'Описание Щитки вратаря CCM EXTREME FLEX 4 PRO, топовая модель линейки EXTREME FLEX 2019 года. Данная модель отличается высоким уровнем защиты, максимальной гибкостью и производительностью. Внешний материал щитков выполнен из высокопрочного синтетического материала SPEEDSKIN. Этот материал обеспечивает максимально быстрое скольжения во время игры. Внутренний двойной сердечник использованный в данной модели, позволил уменьшить вес щитка на 15% в сравнении с предыдущей коллекцией. Сердечник дает ощущение торсионной гибкости под коленом. Это значит, что щиток повторит за вами все движения. Над коленом расположен более жесткий сердечник, который обеспечивает устойчивость. Ботинок имеет угол излома в 90 градусов. Наколенник, выполнен по технологии трехмерного литья и обеспечивает эффективную амортизацию и дополнительное удобство. В сравнении с щитками EXTREME FLEX 4.9, модель EXTREME FLEX 4 PRO будет иметь более качественную конструкцию с использованием материалов более высокого уровня. Особенности: ✅Уровень – Элитный ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка EXTREME FLEX', '/root/images/Щитки вратаря/CCM_EXTREME FLEX 4 PRO_SR.png', null, 'EXTREME FLEX 4 PRO', 2, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (742, 'SR', '', 'Описание Щитки BAUER SUPREME ULTRASONIC, топовая модель линейки SUPREME 2020 года. Щитки оснащены новой конструкцией SHOCKLITE 151, которая позволяет максимально точно управлять отскоком шайбы. Нижняя часть щитка имеет подвижную конструкцию CONNECT FLEX 125, которая обеспечивает комфорт при передвижении. В области бедра используется композитный материал CURV, благодаря этому щиток имеет очень легкий вес и стал более прочным и гибким. Для лучшего скольжения внешняя часть щитка, включая пластины в области колена и голени покрыты материалом C.O.R.TECH ST. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. Основные особенности: ✅Уровень – Элитный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – C.O.R.T.E.C.H ST ✅Ремни – TUNE FIT Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Щитки вратаря/BAUER_SUPREME ULTRASONIC_SR.png', null, 'SUPREME ULTRASONIC', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (743, 'SR', '', 'Описание Щитки BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Щитки оснащены конструкцией DINAMIC FLEX и ботинком с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. В области бедра используется композитный материал CURV, благодаря этому щиток имеет очень легкий вес и стал более прочным и гибким. Для лучшего скольжения внешняя часть щитка, включая пластины в области колена и голени покрыты материалом C.O.R.TECH ST. Для защиты икроножной мышцы используется пена AEROLITE. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Элитный ✅Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – C.O.R.T.E.C.H ST; Искусственная кожа ✅Колено – AX SUEDE QUTTRO+ ✅Ремни – TUNE FIT ✅Защита икры - AEROLITE Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Щитки вратаря/BAUER_VAPOR 2X PRO_SR.png', null, 'VAPOR 2X PRO', 1, 23);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (744, 'YTH', '', 'Описание Шлем вратаря BAUER PROFILE 930, любительская модель линейки PROFILE 2020 года. Шлем предназначен для начинающих вратарей и вратарей-любителей. Внешнее покрытие шлема выполнено из поликарбоната LEXAN EXL, который обладает высокой прочностью. Внутренняя подкладка выполнена из мягкой пены COMFORT, которая обеспечивает непревзойденное удобство, легко подстраивается под форму головы и максимально защищает от любых ударов. Текстильная лента из влагопоглощающего материала, расположенная в области лба, исключает попадание влаги в глаза вратаря. Особенности: ✅Уровень – Любительский ✅Внешнее покрытие – LEXAN EXL ✅Ключевые Технологии – Пена COMFORT Характеристики Возрастная группа YTH', '/root/images/Шлем вратаря/BAUER_PROFILE 930_YTH.png', null, 'PROFILE 930', 1, 24);
