INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (747, 'JR', '', 'Описание Шлем вратаря BAUER PROFILE 940X, любительская модель линейки PROFILE 2018 года. Внутренняя пена шлема выполнена из мягкого, перфорированного материала, что обеспечивает максимальную вентиляцию. Этот шлем отличается легкой внешней оболочкой из поликарбоната LEXAN, максимально поглощающий удары шайбы и удобной защитой подбородка. Вратарская маска имеет удлиненную форму. Шлем данной модели подходит для детей и вратарей-любителей. Особенности: • Уровень – Любительский • Внешнее покрытие – Поликарбонат LEXAN • Ключевые технологии – VN FOAM Характеристики Возрастная группа JR', '/root/images/Шлем вратаря/BAUER_PROFILE 940X_JR.png', null, 'PROFILE 940X', 1, 24);
