INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (749, 'SR', '', 'Описание Шлем вратаря BAUER NME-IX, профессиональная модель линейки NME 2019 года. Это новинка 2019 года, который вышел на смену модели NME8. Внешнее покрытие шлема POWERCORE выполнено из углеродного волокна и эпоксидной смолы, за счет чего отлично поглощает удары в области лба и подбородка. Шлем оснащен внутренней подкладкой элитного уровня VTX, которая надежно защищает. Особенности: • Уровень – Профессиональный • Внешнее покрытие – POWERCORE • Ключевые Технологии – VTX; PORON XRD; THERMOFORMED IX Характеристики Возрастная группа SR', '/root/images/Шлем вратаря/BAUER_NMEIX_SR.png', null, 'NMEIX', 1, 24);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (750, 'SR', '', 'Описание Шлем вратаря BAUER PROFILE 960XPM, топовая модель линейки PROFILE . Один из самых лучших вратарских шлемов и самый популярный шлем в NHL. Внешняя оболочка шлема выполнена из стеклопластика плотностью 12К и дополнена усиленной амортизирующей лентой 3М для максимального смягчения ударов шайбой. В качестве внутренней пены используется PORON XRD, которая обеспечивает максимальный комфорт и рассеивает энергию. Подбородник изготовлен из материала PORON XRD Особенности: • Уровень – Топовый • Внешнее покрытие – Карбон 12K; INNEGRA • Ключевые технологии – VN FOAM; PORON XRD • Внутренняя подкладка – 37,5 Характеристики Возрастная группа SR', '/root/images/Шлем вратаря/BAUER_PROFILE 960XPM_SR.png', null, 'PROFILE 960XPM', 1, 24);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (751, 'SR', '', 'Описание Шлем вратаря BAUER NME VTX, топовая модель линейки NME 2018 года. Внешнее покрытие шлема выполнено из углеродного волокна TeXtreme, которое обладает отличными защитными свойствами и имеет очень легкий вес. Внутренняя подкладка пена PORON XRD, которая поглощает энергию и технология VTX. Подбородник так же выполнен из пены PORON XRD. Подкладка на внутреннем вкладыше в области лба с технологией 37,5 препятствует попаданию влаги в глаза и быстро сохнет. Особенности: • Уровень – Элитный • Внешнее покрытие – TeXtreme • Ключевые технологии – VTX; XRD PORON; TeXtreme; THERMOFORMED IX Характеристики Возрастная группа SR', '/root/images/Шлем вратаря/BAUER_NME VTX_SR.png', null, 'NME VTX', 1, 24);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (752, 'SR', '', 'Описание Шлем вратаря BAUER PROFILE 960, топовая модель линейки PROFILE 2020 года. В этом году BAUER запускает новый шлем в своей самой популярной линейки PROFILE среди профессиональных вратарей. Главное особенностью шлема стала максимальная защита и комфорт. Данная модель шлема оснащена новой эпоксидной смолой POWERCORE, которая обеспечивает превосходное гашение вибрации. Внешняя оболочка шлема выполнена из композитного материала SMACWRAP, который обеспечивает максимальную защиту и легкий вес. В качестве внутренней подкладки используется новый материал AirFlow, который обеспечивает лучшую воздухопроницаемость. В стратегически важных местах для большей защиты используется пена PORON XRD и IX-FOAM. Так же внутренняя подкладка обработана по технологии THERMO CORE, которая эффективно отводит влагу. Особенности: ✅Уровень – Топовый ✅Внешнее покрытие – SMACWRAP ✅Ключевые технологии – XRD PORON; IX-FOAM; AirFlow ✅Внутренняя подкладка – THERMO CORE Характеристики Возрастная группа SR', '/root/images/Шлем вратаря/BAUER_PROFILE 960_SR.png', null, 'PROFILE 960', 1, 24);
