INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (755, 'YTH', '', 'Описание Блин с ловушкой BAUER GSX PRODIGY, детская, любительская модель 2020 года. Блин данной модели предназначена для детей. Он имеет гибкую манжету, как в линейки VAPOR, а материалы используются от линейки SUPREME. Новый размер ладошки позволяет увеличить мобильность при игре клюшкой. Монолитная конструкция блина обеспечивает лучшую износостойкость. Ладонь выполнена из высококачественного материала NASH, который обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Любительский ✅Ладонь - NASH ✅Лицевая сторона – Пена средней плотности ✅Подкладка – NASH Ловушка данной модели предназначена для детей. Посадка данной модели аналогична линейке VAPOR, а карман ловушки выполнен как в SUPREME. Ловушка оснащена двойным Т-образным карманом. Так же производитель увеличил размер кармана для большего закрытия ворот и легкости закрывания ловушки. В качестве внутренней подкладки используется материал NASH, который обеспечивает повышенный комфорт. Особенности: ✅Уровень – Любительский ✅Манжета – Монолит ✅Подкладка – AIR MESH ✅Карман – Двойной Т-образный ✅Внешний материал – Пена средней плотности Характеристики Возрастная группа YTH', '/root/images/Блин-ловушка вратаря/BAUER_GSX PRODIGY_YTH.png', null, 'GSX PRODIGY', 1, 25);
