INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (757, 'YTH', '', 'Описание Блин с ловушкой CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Блин с ловушкой подходят для начинающих вратарей. Данная модель очень мобильна и удобна. Внешний материал комплекта SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Так же блин с ловушкой имеют очень легкий вес. Ловушка имеет невероятную гибкость, благодаря этому вратарь очень легко сможет ее сомкнуть. Конструкция ловушки состоит из двух частей для большей гибкости в области запястья. Угол закрытия ловушки – 60 градусов, придает вратарю более легкое закрытие и обеспечивает комфорт во время использования. Конструкция блина позволяет вратарю точно и быстро перенаправить шайбу в углы. Блокировочная панель выполнена из пены низкой плотности и пластика, благодаря этому блин имеет легкий вес. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Возраст – Дети (4-7 лет) ✅Материал – SPEEDSKIN ✅Конструкция блина – Цельная ✅Ладонь - NASH ✅Конструкция ловушки – Двухсоставная, для большей гибкости. ✅Угол закрытия – 60 градусов Характеристики Возрастная группа YTH', '/root/images/Блин-ловушка вратаря/CCM_YFLEX 2_YTH.png', null, 'YFLEX 2', 2, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (758, 'JR', '', 'Описание Блин с ловушкой BAUER GX, любительская модель 2020 года. Блин данной модели предназначена для детей и игроков-любителей. Новый размер ладошки позволяет увеличить мобильность при игре клюшкой. Монолитная конструкция блина обеспечивает лучшую износостойкость. Новая манжета FREE-FLEX, обеспечивает максимальную свободу движения. Ладонь выполнена из высококачественного материала NASH, который обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Любительский ✅Ладонь - NASH ✅Лицевая сторона – Пена низкой плотности ✅Подкладка – Влагоотталкивающая Ловушка данной модели предназначена для детей и игроков-любителей. Данная модель ловушки выполнена по аналогии линейки SUPREME. Угол закрытия – 75 градусов, придает вратарю более легкое закрытие и обеспечивает комфорт во время использования. Ловушка оснащена двойным Т-образным карманом. Особенности: ✅Уровень – Любительский ✅Манжета – Монолит ✅Подкладка – Влагоотталкивающая ✅Карман – Двойной Т-образный ✅Внешний материал – Пена низкой плотности Характеристики Возрастная группа JR', '/root/images/Блин-ловушка вратаря/BAUER_GX_JR.png', null, 'GX', 1, 25);
