INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (763, 'JR', '', 'Описание Блин с ловушкой BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Блин данной модели имеет новую форму, а блокировочная панель имеет монолитную конструкцию, которая повышает устойчивость блина и помогает вратарю отбивать шайбы в угол. Так же за счет монолитной конструкции блин имеет очень легкий вес и максимальное удобство. Особенности: ✅Уровень – Любитель ✅Манжета – Запатентованная технология FREE-FLEX ✅Ладонь - NASH ✅Лицевая сторона – Пена средней плотности; монолит ✅Подкладка – Влагоотталкивающий нейлон Ловушка линейки VAPOR сгибаются под углом 60 градусов и закрывается за счет смыкания всей ладони. Данная модель оснащена новой манжетой, которая обеспечивает большую амплитуду движения. При изготовлении ловушки использовались новые материалы, чтобы сделать перчатку гибкой и удобной. Помимо этого, была увеличены глубина кармана. Особенности: ✅Уровень – Любительский ✅Ключевые изменения – Новый глубокий карман; Новый ремень FAST STRAP; Манжета FREE-FLEX ✅Манжета – Монолит ✅Подкладка – NASH ✅Карман – Т-образный ✅Окантовка – Пена средней плотности Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Блин-ловушка вратаря/BAUER_VAPOR X2.7  FRT_JR.png', null, 'VAPOR X2.7  FRT', 1, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (764, 'INT', '', 'Описание Блин с ловушкой CCM PREMIER P2.9, полупрофессиональная модель линейки PREMIER 2018 года. Ловушка данной модели имеет цельную конструкцию и угол закрытия кармана 60 градусов. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для большей защиты в области ладони используется войлок плотностью 6 мм. Область манжеты выполнена из высококачественного полиэтилена, который максимально поглощает энергию удара. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Профессиональный ✅Угол закрытия – 60 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена средней плотности Блин данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для максимальной защиты тыльной стороны используется пена высокой плотности с вставкой из пластика. В области пальцев для большей защиты используется пена высокой. Область запястья имеет конструкцию ONE-PIECE CUFF. Эта конструкция выполнена из пены, которая оборачивает полностью руку и заполняет негативное пространство. Ладонь выполнена из материала NASH SURE-GRIP, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Профессиональный ✅Манжета – ONE-PIECE CUFF ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена высокой плотности; Пластик ✅Защита пальцев – Пена средней плотности Характеристики Возрастная группа INT Линейка PREMIER', '/root/images/Блин-ловушка вратаря/CCM_PREMIER P2.9_INT.png', null, 'PREMIER P2.9', 2, 25);
