INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (770, 'JR', '', 'Описание Блин с ловушкой CCM AXIS 1.5, юниорская полупрофессиональная модель линейки AXIS 2020 года. Ловушка AXIS обеспечивает невероятную защиту и комфорт во время игры. Ловушка имеет угол закрытия кармана 60 градусов, как и в предыдущей линейки PREMIER II. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Цельная конструкция манжеты и большого пальца обеспечивает вратарю большую амплитуду движения и максимальный обхват. Для большей защиты в области ладони используется войлок и пена низкой плотности. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Особенности: ✅Уровень – Полупрофессиональный ✅Угол закрытия – 60 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена низкой плотности Блин AXIS вышел на замену PREMIER II, но при этом сохранил большинство технологий от предыдущей модели. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для максимальной защиты тыльной стороны используется пена низкой плотности и полиэтилен. В области пальцев для большей защиты используется пена низкой плотности. Область запястья имеет трехсоставную конструкцию 3-PIECE CUFF, обеспечивая максимальную гибкость и расширенный диапазон движения. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Полупрофессиональный ✅Манжета – 3-PIECE CUFF ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена низкой плотности; Полиэтилен ✅Защита пальцев – Пена низкой плотности; Характеристики Возрастная группа JR Линейка AXIS', '/root/images/Блин-ловушка вратаря/CCM_AXIS 1.5_JR.png', null, 'AXIS 1.5', 2, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (771, 'JR', '', 'Описание Блин с ловушкой CCM EXTREME FLEX 4.5, модель линейки EXTREME FLEX 2019 года. Конструкция блина позволяет вратарю точно и быстро перенаправить шайбу в углы. Внешний материал данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. В области тыльной стороны используется пена низкой плотности с вставкой из пластика. В области пальцев для большей защиты используется пена средней плотности. Для более быстрой регулировки и максимального комфорта в области запястья CCM добавил быстросъемный ремешок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Любительский ✅Манжета – Сегментированная, регулируемая ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена низкой плотности; Пластик ✅Защита пальцев – Пена средней плотности Данная модель имеет цельную конструкцию манжеты и указательного пальца, что дает лучший контроль рикошета шайбы при попадании в ловушку. Внешний материал данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Для большей защиты ладони используется войлок толщиной 6 мм и пена средней плотности. Для максимального комфорта и более мягкого ощущения при использовании в области пальцев используется материал NASH. Для более быстрой регулировки в области запястья CCM добавил быстросъемный ремешок. Угол закрытия – 75 градусов, придает вратарю более легкое закрытие и обеспечивает комфорт во время использования. Особенности: ✅Уровень – Любительский ✅Ключевые изменения – Новый глубокий карман; Новый ремень для более быстрой регулировки; угол 75 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена низкой плотности Характеристики Возрастная группа JR Линейка EXTREME FLEX', '/root/images/Блин-ловушка вратаря/CCM_EXTREME FLEX E4.5_JR.png', null, 'EXTREME FLEX E4.5', 2, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (772, 'SR', '', 'Описание Блин с ловушкой BAUER GSX, любительская модель 2020 года. Блин данной модели предназначена для детей и игроков-любителей. Он имеет гибкую манжету, как в линейки VAPOR, а материалы используются от линейки SUPREME. Новый размер ладошки позволяет увеличить мобильность при игре клюшкой. Монолитная конструкция блина обеспечивает лучшую износостойкость. Ладонь выполнена из высококачественного материала NASH, который обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Любительский ✅Ладонь - NASH ✅Лицевая сторона – Пена средней плотности ✅Подкладка – NASH Ловушка данной модели предназначена для детей и игроков-любителей. Посадка данной модели аналогична линейке VAPOR, а карман ловушки выполнен как в SUPREME. Ловушка оснащена двойным Т-образным карманом. В качестве внутренней подкладки используется материал AIR MESH, который хорошо отводит влагу. Особенности: ✅Уровень – Любительский ✅Манжета – Монолит ✅Подкладка – AIR MESH ✅Карман – Двойной Т-образный ✅Внешний материал – Пена средней плотности Характеристики Возрастная группа SR', '/root/images/Блин-ловушка вратаря/BAUER_GSX_SR.png', null, 'GSX', 1, 25);
