INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (774, 'SR', '', 'Описание Блин с ловушкой CCM PREMIER P2.5, любительская модель линейки PREMIER 2018 года. Ловушка данной модели имеет цельную конструкцию и угол закрытия кармана 60 градусов. Внешний материал данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для большей защиты в области ладони используется войлок плотностью 6 мм. Область манжеты выполнена из высококачественного полиэтилена, который максимально поглощает энергию удара. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Уровень – Любительский ✅Угол закрытия – 60 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена низкой плотности Блин данной модели выполнен из синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для большей защиты тыльной стороны используется смесь из пен разной плотности. В области пальцев используется пена низкой плотности. Область запястья имеет конструкцию ONE-PIECE CUFF. Эта конструкция выполнена из пены, которая оборачивает полностью руку и заполняет негативное пространство. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Любительский ✅Манжета – ONE-PIECE CUFF ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена разной плотности ✅Защита пальцев – Пена низкой плотности В сравнении с комплектом блин с ловушкой PREMIER P2.9, модель PREMIER P2.5 будет иметь менее качественную конструкцию с использованием материалов более низкого уровня. Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Блин-ловушка вратаря/CCM_PREMIER P2.5_SR.png', null, 'PREM