INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (786, 'SR', '', 'Описание Блин с ловушкой CCM AXIS, топовая модель линейки AXIS 2020 года. Ловушка AXIS обеспечивает невероятную защиту и комфорт во время игры. Ловушка имеет угол закрытия кармана 60 градусов, как и в предыдущей линейки PREMIER II. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Цельная конструкция манжеты и большого пальца обеспечивает вратарю большую амплитуду движения и максимальный обхват. Для максимальной защиты в области ладони используется войлок и пена D3O, которая поглощает энергию удара. Область манжеты выполнена из высококачественного вспененного полиэтилена. Ловушка имеет двойной Т-образный карман, для повышения видимости и эффективного смыкания. Особенности: ✅Уровень – Элитный ✅Угол закрытия – 60 градусов ✅Манжета – Цельная ✅Внешний материал - SPEEDSKIN ✅Карман – Двойной Т-образный ✅Окантовка – Пена средней плотности Блин AXIS вышел на замену PREMIER II, но при этом сохранил большинство технологий от предыдущей модели. Внешний материал данной модели выполнен из высокопрочного синтетического материала SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Для максимальной защиты тыльной стороны используется пена высокой плотности с вставкой из полиэтилена. В области пальцев для большей защиты используется пена высокой плотности и пены D3O, которая максимально поглощает энергию удара. Область запястья имеет трехсоставную конструкцию 3-PIECE CUFF, обеспечивая максимальную гибкость и расширенный диапазон движения. Ладонь выполнена из многослойного армированного материала NASH, которая обладает максимальным удобством и прочностью Особенности: ✅Уровень – Элитный ✅Манжета – 3-PIECE CUFF ✅Ладонь - NASH ✅Внешний материал – SPEEDSKIN ✅Лицевая сторона – Пена высокой плотности; Полиэтилен ✅Защита пальцев – Пена высокой плотности; D3O Характеристики Возрастная группа SR Линейка AXIS', '/root/images/Блин-ловушка вратаря/CCM_AXIS_SR.png', null, 'AXIS', 2, 25);
