INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (788, 'SR', '', 'Описание Блин с ловушкой BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Одно из главных изменений стало, то что блокировочная панель имеет монолитную конструкцию, за счет этого повышается устойчивость блина. Выполнена блокировочная панель из композитного материала CURV, благодаря этому блин стал гораздо легче и прочнее. Внешнее покрытие блина C.O.R.TECH ST, которое позволяет лучше контролировать отскок шайбы. Для максимального удобства ладонь изготовлена из очень мягкого и надежного материала QUATTRO, а благодаря внутренней подкладки THERMOCORE руки вратаря остаются сухими во время игры. Особенности: ✅Уровень – Элитный ✅Манжета – Запатентованная технология FREE-FLEX ✅Ладонь - QUATTRO ✅Внешний материал – C.O.R.TECH ST ✅Лицевая сторона – CURV; монолит ✅Подкладка – THERMOCORE Ловушка линейки VAPOR сгибаются под углом 60 градусов и закрывается за счет смыкания всей ладони. Данная модель оснащена новой манжетой, которая обеспечивает большую амплитуду движения. Внешняя часть и манжета выполнена из композитного материала CURV, благодаря чему ловушка стала более прочной и легкой. Для защиты ладони используется пена PORON XRD, который дополнительно защищает руку от мощных ударов. Помимо этого, была увеличены глубина кармана. Особенности: ✅Уровень – Элитный ✅Ключевые изменения – Новый глубокий карман; Новый ремень FAST STRAP; Манжета FREE-FLEX с композитным материалом CURV; Ладонь PORON XRD ✅Манжета – CURV; FREE-FLEX ✅Внешний материал - CATCHLITE ✅Подкладка – THERMOCORE ✅Карман – Т-образный ✅Окантовка – Пена средней плотности Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Блин-ловушка вратаря/BAUER_VAPOR 2X PRO_SR.png', null, 'VAPOR 2X PRO', 1, 25);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (789, 'SR', '', 'Описание Блин с ловушкой BAUER SUPREME ULTRASONIC, топовая модель линейки SUPREME 2020 года. Блины линейки SUPREME отличаются монолитной конструкцией от основной плиты до торцевой части. Выполнена блокировочная панель из композитного материала CURV, благодаря этому блин стал гораздо легче и прочнее. Внешнее покрытие блина SHOCKLITE. Для максимального удобства ладонь изготовлена из очень мягкого и надежного материала QUATTRO, а благодаря внутренней подкладки THERMO MAX+ руки вратаря остаются сухими во время игры. Особенности: ✅Уровень – Топовый ✅Ладонь - QUATTRO ✅Лицевая сторона – CURV; SHOCKLITE ✅Подкладка – THERMO MAX+ Ловушка оснащена двойным Т-образным карманом и жесткой манжетой за счет этого она легко открывается и закрывается. Карман ловушки увеличен за счет новой системы шнуровки. Внешняя часть и манжета выполнена из композитного материала CURV, благодаря чему ловушка стала более прочной и легкой. Для защиты ладони используется пена PORON XRD, который дополнительно защищает руку от мощных ударов. Особенности: ✅Уровень – Топовый ✅Манжета – Монолит ✅Подкладка – THERMOCORE ✅Карман – Двойной Т-образный ✅Окантовка – CURV Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Блин-ловушка вратаря/BAUER_SUPREME ULTRASONIC_SR.png', null, 'SUPREME ULTRASONIC', 1, 25);
