INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (798, 'JR', '', 'Описание Защита горла вратаря BAUER S18 SUPREME Основные особенности: ✅Защита горла выполнена из пены двойной плотности, обеспечивая максимальный уровень защиты ✅Воротник имеет сегментированное строение для большей мобильности. В качестве защитного материала используется пена FleXorb ✅Внутренний материал выполнен по технологии 37,5® и Sanitized® ✅Размер - 26-31 см ✅Подходит на возраст примерно от 5 до 11 лет ✅Сертифицирована Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Защита горла вратаря/BAUER_S18 SUPREME_JR.png', null, 'S18 SUPREME', 1, 27);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (799, 'SR', '', 'Описание Защита горла вратаря AEGIS INTERCEPTOR GOALIE SR AEGIS INTERCEPTOR G – самая гибкая, удобная, защищенная и ориентировочная на производительность защита горла для вратарей. Внешний материал выполнен из прочного нейлонового спандекса и кевларовой нити, которая максимально защищает шею от порезов. Защитные элементы выполнены из высококачественной пены D3O, которая максимально защищает шею от удара за счет рассеивания энергии, при этом сохраняя полный диапазон движения. Характеристики Возрастная группа SR', '/root/images/Защита горла вратаря/AEGIS_INTERCEPTOR GOALIE_SR.png', null, 'INTERCEPTOR GOALIE', 26, 27);
