INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (802, 'INT', '', 'Описание Раковина вратаря CCM 500 Вешняя отделка раковины выполнена из мягких, воздухопроницаемых материалов, отличающихся высокой износоустойчивостью. Раковина комплектуется стандартной, защитной чашей из формованного пластика, которая покрывается мягким, вспененным материалом, что создает эффективную защиту области паха и способствует комфортному прилеганию чаши к телу. Дополнительную защиту для паха и внутренней области бедра создают специальные накладки из вспененного материала, которые способствуют более мягкому контакту элементов защиты с телом, существенно повышают комфорт. Передняя панель, защищающая нижнюю часть живота и область тазобедренных суставов, выполнена из вспененного материала и имеет удобную, эргономичную форму. Сегментированное строение передней панели не ограничивает движения вратаря и способствует комфортной и плотной посадке раковины на теле. Подходит примерно на возраст от 10 до 14 лет Характеристики Возрастная группа INT', '/root/images/Защита паха вратаря/CCM_500_INT.png', null, '500', 2, 28);
