INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (805, 'JR', '', 'Описание Раковина вратаря BAUER VAPOR Выполнена с использованием технологии AEROLITE обеспечивает максимальный уровень защиты, сохраняя максимальное чувство легкости и комфорта. Прочные ремни позволяют закрепить раковину и удерживать ее на месте на протяжении всей игры. Внутренняя подкладка с технологией 37.5 обеспечивает максимальный уровень комфорта. Подходит примерно на возраст от 6 до 13 лет Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Защита паха вратаря/BAUER_VAPOR_JR.png', null, 'VAPOR', 1, 28);
