INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (807, 'SR', '', 'Описание Раковина вратаря BAUER VAPOR Выполнена с использованием технологии AEROLITE обеспечивает максимальный уровень защиты, сохраняя максимальное чувство легкости и комфорта. Прочные ремни позволяют закрепить раковину и удерживать ее на месте на протяжении всей игры. Внутренняя подкладка с технологией 37.5 обеспечивает максимальный уровень комфорта. Подходит примерно на возраст от 13 лет Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Защита паха вратаря/BAUER_VAPOR_SR.png', null, 'VAPOR', 1, 28);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (808, 'SR', '', 'Описание Раковина вратаря CCM 500 Вешняя отделка раковины выполнена из мягких, воздухопроницаемых материалов, отличающихся высокой износоустойчивостью. Раковина комплектуется стандартной, защитной чашей из формованного пластика, которая покрывается мягким, вспененным материалом, что создает эффективную защиту области паха и способствует комфортному прилеганию чаши к телу. Дополнительную защиту для паха и внутренней области бедра создают специальные накладки из вспененного материала, которые способствуют более мягкому контакту элементов защиты с телом, существенно повышают комфорт. Передняя панель, защищающая нижнюю часть живота и область тазобедренных суставов, выполнена из вспененного материала и имеет удобную, эргономичную форму. Сегментированное строение передней панели не ограничивает движения вратаря и способствует комфортной и плотной посадке раковины на теле. Подходит примерно на возраст от 14 лет Характеристики Возрастная группа SR', '/root/images/Защита паха вратаря/CCM_500_SR.png', null, '500', 2, 28);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (809, 'SR', '', 'Описание Раковина вратаря BAUER S18 SUPREME SR Раковина обеспечивает защиту профессионального уровня и выполнена из легких и долговечных материалов, обладает хорошей мобильностью, не ограничивает движений вратаря. Внешняя отделка бандажа выполнена из износостойкого нейлона. Передняя панель раковины, закрывающая и защищающая нижнюю часть живота и область тазобедренных суставов, имеет эргономичную форму и сегментированную конструкцию, которая позволяет вратарю свободно двигаться. Пена двойной плотности используется в качестве амортизирующего наполнения передней панели. Вставка, выполненная по технологии PORON XRD, обеспечивает вратарю максимальный уровень защиты. ополнительную защиту для паха и внутренней области бедра создают комфортные накладки COMFORT EDGE из вспененного материала, которые способствуют более мягкому контакту элементов защиты с телом, существенно повышают комфорт при использовании раковины. Подходит примерно на возраст от 14 лет Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Защита паха вратаря/BAUER_S18 SUPREME_SR.png', null, 'S18 SUPREME', 1, 28);
