INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (821, 'JR', '', 'Описание Защита коленей вратаря BAUER S18 SUPREME, модель 2018 года. Основные особенности: ✅Наколенники выполнены из высококачественного пластика с добавлением материала CURV ✅Для максимальной защиты в области коленной чашки используется пена FleXorb и Vent Armor ✅Внутренний материал выполнен по технологии 37,5® и Sanitized®, гарантирует правильный воздухообмен, быстрое испарение влаги и отсутствие неприятного запаха. ✅Подходит на возраст примерно от 9 до 14 лет ✅Наколенник фиксируется на ноге с помощью четырех ремней с застежкой-липучкой. Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Защита коленей вратаря/BAUER_S18 SUPREME_JR.png', null, 'S18 SUPREME', 1, 29);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (822, 'JR', '', 'Описание Защита коленей вратаря BAUER VAPOR 1X, модель 2017 года. Основные особенности: ✅Наколенники выполнены из пены AEROLITE и композитного материала CURV ✅Для максимальной защиты в области коленной чашки используется композитный материал CURV ✅Внутренний материал выполнен по технологии Transfer mesh и 37.5® Technology, гарантирует правильный воздухообмен, быстрое испарение влаги и отсутствие неприятного запаха. ✅Подходит на возраст примерно от 9 до 14 лет ✅Наколенник фиксируется на ноге с помощью трех ремней с застежкой-липучкой. Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Защита коленей вратаря/BAUER_VAPOR 1X_JR.png', null, 'VAPOR 1X', 1, 29);
