INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (969, 'YTH', '', 'Описание Коньки BAUER VAPOR S17 X300 - это любительская модель в линейке VAPOR 2017 года. Похоже, некоторые игроки просто знают, куда полетит шайба. Такие игроки всегда на один шаг опережают своих соперников — они находятся в эпицентре момента, а не наблюдают со стороны. Если это про вас, проверьте коньки X300 с композитными интегрированными вставками из волокна quarter fit. Они быстрые. Маневренные. И готовы к игре. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитные интегрированные вставки из волокна quarter fit • Материал подкладки - Микрофибра • Защита лодыжки - Подушки для лодыжки из анатомического пеноматериала. • Конструкция языка - Анатомической формы, из двух частей, из войлока • Стелька - Стандартный комфорт • Термоформуемость - Терморегулируемый верх НИЖНЯЯ ЧАСТЬ: • Стакан (держатель для лезвия) - TUUK LIGHTSPEED Pro • Лезвие - Нержавеющая сталь TUUK Характеристики Возрастная группа YTH Линейка VAPOR', '/root/images/Шайба/BAUER_VAPOR S17 X300_YTH.png', null, 'VAPOR S17 X300', 1, 43);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (970, 'SR', '', 'Описание Защита горла вратаря CCM LEXAN THROAT GUARD, модель 2018 года Защита горла выполена из литого под давлением материала LEXAN, который отличается высокой прочностью и долговечностью.Защита горла улучшает свободу движений. Прозрачный материал с противотуманным покрытием, чтобы шайба оставалась на виду. ✅Подходит примерно на рост 160+ Характеристики Возрастная группа SR', '/root/images/Шайба/CCM_LEXAN THROAT GUARD_SR.png', null, 'LEXAN THROAT GUARD', 2, 43);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (971, 'SR', '', 'Описание Щитки вратаря CCM PREMIER II PRO SR, топовая модель линейки PREMIER 2018 года. Внешний материал щитков SPEEDSKIN, который лучше скользит по льду. Внутренняя конструкция щитков выполнена по новой технологии, благодаря этому они весят на 20% меньше, чем предыдущая коллекция. В основу этих щитков положена технология MAX REBOUND (максимальный отскок). По сути это вспененный материал, при попадании в который шайба отскакивает далеко в поле. Так же в этих щитках изменена нижняя часть, закрывающая конек, теперь вратарю будет проще отталкиваться ото льда, стоя на коленях. С обратной стороны щитки оснащены эластичными ремешками STRAP SYSTEM, благодаря чему вратарю проще садиться в бабочку и вставать на ноги. Особенности: ✅Уровень – Элитный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка PREMIER', '/root/images/Шайба/CCM_PREMIER II PRO_SR.png', null, 'PREMIER II PRO', 2, 43);
